import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import MainLayout from './MainLayout';
import Dashboard from './Dashboard';
import Tareas from './Tareas';

function AppRoutes({ token, onLogout, taskToEdit, setTaskToEdit, onTaskUpdate, refreshKey, movimientos, ...authProps }) {
  return (
    <Routes>
      <Route
        path="/"
        element={
          <MainLayout 
            token={token} 
            onLogout={onLogout} 
            // Pasamos las props de tareas al Layout para el sidebar
            taskToEdit={taskToEdit}
            onTaskUpdate={onTaskUpdate}
          />
        }
      >
        <Route index element={<Dashboard  movimientos={movimientos} token={token} {...authProps} />} />
        <Route
          path="notas"
          element={
            token ? (
              <Tareas 
                token={token} 
                refreshKey={refreshKey} 
                onEditClick={setTaskToEdit} 
              />
            ) : (
              <Navigate to="/" />
            )
          }
        />
      </Route>
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default AppRoutes;
