import React from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import MainLayout from "./MainLayout";
import Dashboard from "./Dashboard";
import Add from "./Add";
import Tareas from "./Tareas";

// ✅ Cambiá estos imports por tus componentes reales
import LoginPage from "./LoginPage";
import SignupPage from "./SignupPage";

function AppRoutes({
  token,
  onLogout,
  movimientos,
  taskToEdit,
  setTaskToEdit,
  onTaskUpdate,
  onUpdate,
  movementToEdit,
  setMovementToEdit,
  refreshKey,
  ...authProps
}) {
  // ✅ 1) Si NO hay token: solo rutas públicas (Login primero)
  if (!token) {
    return (
      <Routes>
        <Route path="/" element={<LoginPage {...authProps} />} />
        <Route path="/login" element={<LoginPage {...authProps} />} />
        <Route path="/register" element={<SignupPage {...authProps} />} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    );
  }

  // ✅ 2) Si HAY token: rutas privadas (tu app)
  return (
    <Routes>
      <Route
        path="/"
        element={
          <MainLayout
            token={token}
            onLogout={onLogout}
            movimientos={movimientos}
            taskToEdit={taskToEdit}
            onTaskUpdate={onTaskUpdate}
            refreshKey={refreshKey}
            onUpdate={onUpdate}
          />
        }
      >
        <Route
          index
          element={
            <Dashboard
              token={token}
              refreshKey={refreshKey}
              onMovementUpdate={onUpdate}
              movementToEdit={movementToEdit}
              setMovementToEdit={setMovementToEdit}
              movimientos={movimientos}
              {...authProps}
            />
          }
        />

        <Route
          path="add"
          element={
            <Add
              onMovementAdded={onUpdate}
              movementToEdit={movementToEdit}
              {...authProps}
            />
          }
        />

        <Route
          path="notas"
          element={
            <Tareas
              token={token}
              refreshKey={refreshKey}
              onEditClick={setTaskToEdit}
            />
          }
        />

        <Route path="*" element={<Navigate to="/" />} />
      </Route>
    </Routes>
  );
}

export default AppRoutes;
