import { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { login as loginService } from '../api/auth';
import style from '../style/Login.module.css';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError(''); // Limpiar error al escribir
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const userData = await loginService(formData);
      login(userData);
      // Redirigir al dashboard o página principal
      window.location.href = '/';
    } catch (err) {
      setError(err.message || 'Error en el login');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={style.loginContainer}>
      <div className={style.loginCard}>
        <h2 className={style.title}>Iniciar Sesión</h2>

        <form onSubmit={handleSubmit} className={style.loginForm}>
          {error && <div className={style.errorMessage}>{error}</div>}

          <div className={style.formGroup}>
            <label htmlFor="email" className={style.label}>Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              disabled={loading}
              className={style.input}
              placeholder="tu@email.com"
            />
          </div>

          <div className={style.formGroup}>
            <label htmlFor="password" className={style.label}>Contraseña:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              disabled={loading}
              className={style.input}
              placeholder="Tu contraseña"
            />
          </div>

          <button 
            type="submit" 
            disabled={loading}
            className={style.loginButton}
          >
            {loading ? 'Iniciando sesión...' : 'Iniciar Sesión'}
          </button>
        </form>

        <p className={style.registerLink}>
          ¿No tienes cuenta? <a href="/register">Regístrate aquí</a>
        </p>
      </div>
    </div>
  );
};

export default Login;
