import axios from "axios";
import { useState, useEffect } from "react";
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import style from '../style/Tarea.module.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

function Tareas({ token, refreshKey, onEditClick }) {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showAll, setShowAll] = useState(false);

  /* ===============================
     FETCH TAREAS POR FECHA
  =============================== */
  useEffect(() => {
    if (!token || showAll) return;

    const fetchTasks = async () => {
      setLoading(true);
      setError('');
      try {
        const date = new Date(selectedDate);
        date.setMinutes(date.getMinutes() - date.getTimezoneOffset());
        const formattedDate = date.toISOString().slice(0, 10);

        const res = await axios.get(
          `${API_URL}/api/task?fecha=${formattedDate}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setTasks(res.data);
      } catch (err) {
        console.error(err);
        setError("No se pudieron cargar las tareas.");
        setTasks([]);
      } finally {
        setLoading(false);
      }
    };

    fetchTasks();
  }, [token, refreshKey, selectedDate, showAll]);

  /* ===============================
     FETCH TODAS LAS TAREAS
  =============================== */
  const handleAllTasks = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${API_URL}/api/task/all`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTasks(res.data);
      setShowAll(true);
    } catch (err) {
      console.error(err);
      setError("No se pudieron cargar todas las tareas.");
    } finally {
      setLoading(false);
    }
  };

  /* ===============================
     ACTIONS
  =============================== */
  const handleToggleComplete = async (taskId, currentStatus) => {
    try {
      const res = await axios.put(
        `${API_URL}/api/task/${taskId}/status`,
        { completada: !currentStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setTasks(prev =>
        prev.map(task => task._id === taskId ? res.data : task)
      );
    } catch (err) {
      console.error("Error al actualizar tarea", err);
    }
  };

  const handleDeleteTask = async (taskId) => {
    if (!window.confirm("¿Eliminar tarea?")) return;

    try {
      await axios.delete(`${API_URL}/api/task/${taskId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTasks(prev => prev.filter(t => t._id !== taskId));
    } catch (err) {
      console.error("Error al borrar tarea", err);
    }
  };

  /* ===============================
     RENDER CARDS
  =============================== */
  const renderCards = () => {
    if (loading) return <p>Cargando tareas...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;
    if (tasks.length === 0) return <p>No hay tareas para esta fecha.</p>;

    return tasks.map(task => (
      <div className={style.taskContainer} key={task._id}>
        <div className={`${style.taskCard} ${task.completada ? style.completed : ''}`}>
          <div className={style.taskHeader}>
            <p className={style.taskMeta}>{task.meta}</p>
            <p className={style.taskUrgency}>Urgencia &gt; {task.urgencia}</p>
          </div>

          <div className={style.taskDetails}>
            <p>Horario: {task.horario || 'Sin horario'}</p>
          </div>

          <div className={style.taskActions}>
            <p>{task.completada ? 'Hecho' : 'Pendiente'}</p>

            <input
              type="checkbox"
              checked={task.completada}
              onChange={() => handleToggleComplete(task._id, task.completada)}
            />

            <button onClick={() => onEditClick(task)}>
              <img src="/edit.png" alt="edit" />
            </button>

            <button onClick={() => handleDeleteTask(task._id)}>
              <img src="/trush.png" alt="delete" />
            </button>
          </div>
        </div>
      </div>
    ));
  };

  /* ===============================
     RENDER TABLE
  =============================== */
  const renderTable = () => {
    if (loading) return <p>Cargando tareas...</p>;

    return (
      <table className={style.movimientosTable}>
        <thead>
          <tr>
            <th>Fecha</th>
            <th>Meta</th>
            <th>Urgencia</th>
            <th>Horario</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map(task => (
            <tr key={task._id}>
              <td>{new Date(task.fecha).toLocaleDateString()}</td>
              <td>{task.meta}</td>
              <td>{task.urgencia}</td>
              <td>{task.horario || '—'}</td>
              <td>{task.completada ? 'Hecho' : 'Pendiente'}</td>
              <td>
                <button onClick={() => onEditClick(task)}>Editar</button>
                <button onClick={() => handleDeleteTask(task._id)}>Borrar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  /* ===============================
     MAIN RENDER
  =============================== */
  return (
    <div className={style.container}>
      <div className={style.header}>
        <h1>{showAll ? 'Todas las Tareas' : 'Mis Tareas'}</h1>

        {!showAll && (
          <DatePicker
            selected={selectedDate}
            onChange={setSelectedDate}
            dateFormat="dd-MM-yyyy"
            className={style.datePicker}
          />
        )}

        {!showAll ? (
          <button onClick={handleAllTasks} className={style.datePicker}>
            Ver todas las tareas
          </button>
        ) : (
          <button onClick={() => setShowAll(false)} className={style.datePicker}>
            Volver a tareas por fecha
          </button>
        )}
      </div>

      <div className={style.tasksList}>
        {showAll ? renderTable() : renderCards()}
      </div>
    </div>
  );
}

export default Tareas;
