import axios from "axios";
import { useState, useEffect } from "react";
import style from '../style/Tarea.module.css';

// Función auxiliar para obtener la fecha de hoy en formato YYYY-MM-DD
const getTodayString = () => {
  const today = new Date();
  // Ajustamos por la zona horaria para evitar problemas de un día antes/después
  today.setMinutes(today.getMinutes() - today.getTimezoneOffset());
  return today.toISOString().slice(0, 10);
};

function Tareas({ token, refreshKey }) {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 1. Nuevo estado para guardar la fecha seleccionada. Inicia con el día de hoy.
  const [selectedDate, setSelectedDate] = useState(getTodayString());

  useEffect(() => {
    const fetchTasks = async () => {
      if (!token) {
        setTasks([]);
        return;
      }
      setLoading(true);
      setError('');
      try {
        // 2. Añadimos la fecha seleccionada como un parámetro en la URL
        const res = await axios.get(`http://localhost:3000/api/task?fecha=${selectedDate}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log(`✅ DATOS RECIBIDOS DEL BACKEND PARA LA FECHA ${selectedDate}:`, res.data);
        setTasks(res.data);
      } catch (err) {
        console.error("Error al obtener las tareas:", err);
        setError("No se pudieron cargar las tareas para esta fecha.");
        setTasks([]);
      } finally {
        setLoading(false);
      }
    };
    fetchTasks();
  // 3. Hacemos que el useEffect se ejecute de nuevo si la fecha seleccionada cambia
  }, [token, refreshKey, selectedDate]);

  // 4. Función para manejar el cambio en el input de fecha
  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
  };

  const renderContent = () => {
    // ... (esta función no cambia)
  };

  return (
    <div className={style.container}>
      {/* 5. Título dinámico y selector de fecha */}
      <div className={style.header}>
        <h1>Mis Tareas para el</h1>
        <input 
          type="date" 
          value={selectedDate} 
          onChange={handleDateChange} 
          className={style.datePicker}
        />
      </div>

      <div className={style.tasksList}>
        {renderContent()}
      </div>
    </div>
  );
}

export default Tareas;
