// App.js

import { useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
import LoginPage from "./LoginPage";
import SignupPage from "./SignupPage";
import style from "../style/App.module.css";

function PrivateRoute({ children }) {
  return children;
}

// Layout anidado
// Acepta las nuevas props con nombres más claros
function PrivateLayout({ onLoginClick, onRegisterClick, activeView }) {
  return (
    <div className={style.container}>
      <Nav />
      <div className={style.content}>
        <LeftsSite />
        <div className={style.contentSide}>
          {/* Pasa las nuevas props directamente a Results */}
          <Results
            onLoginClick={onLoginClick}
            onRegisterClick={onRegisterClick}
            activeView={activeView} // Pasa el estado actual
          />
          <Add />
        </div>
      </div>
    </div>
  );
}


function App() {
  // 1. EL CAMBIO MÁS IMPORTANTE: El estado ahora guarda un string.
  // null = no mostrar nada, 'login' = mostrar login, 'signup' = mostrar registro
  const [activeView, setActiveView] = useState(null);

  // 2. Funciones específicas para cada acción
  const handleLoginClick = () => {
    setActiveView('login');
  };

  const handleRegisterClick = () => {
    setActiveView('signup');
  };

  return (
    <Routes>
      <Route path="/login" element={<LoginPage />} />
      <Route path="/register" element={<SignupPage />} />

      <Route
        path="/"
        element={
          <PrivateRoute>
            {/* 3. Pasamos las nuevas funciones y el nuevo estado al layout */}
            <PrivateLayout
              onLoginClick={handleLoginClick}
              onRegisterClick={handleRegisterClick}
              activeView={activeView}
            />
          </PrivateRoute>
        }
      />

      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
