  const [selectedDate, setSelectedDate] = useState(new Date());

  // 2. ELIMINAMOS el useEffect que buscaba los movimientos. App.js lo hace ahora.
  //    (Aquí estaba la fuente de tus "errores de conexión" que dejaban todo en cero)

