import { useState } from "react";
import axios from "axios";
import style from '../style/Add.module.css';
import InputMonto from "./InputMonto"; 

function Add() {
  const [fecha] = useState(new Date().toISOString().split("T")[0]);

  // Estados para controlar el valor de cada input de monto
  const [ingresoMonto, setIngresoMonto] = useState('');
  const [egresoMonto, setEgresoMonto] = useState('');

  // Modificamos handleSubmit para recibir el monto desde el estado
  const handleSubmit = async (e, tipo, monto) => {
    e.preventDefault();
    const form = e.target;

    // 1. Limpiar el monto antes de enviarlo a la API
    // El estado ya lo tiene como "1234.56", pero lo convertimos a número
    const montoNumerico = parseFloat(monto);

    if (isNaN(montoNumerico) || montoNumerico <= 0) {
      alert("Por favor, ingresa un monto válido.");
      return;
    }

    const data = {
      tipo,
      monto: montoNumerico, // Enviamos el número limpio
      categoria: form.categoria.value,
      fecha: form.fecha.value,
      detalle: form.detalle.value
    };

    try {
      const res = await axios.post("http://localhost:3000/api/add", data);

      if (res.status === 201) {
        alert(`${tipo} añadido con éxito`);
        form.reset(); // Resetea los otros campos del formulario
        // Resetea el estado del monto correspondiente
        if (tipo === "ingreso") {
          setIngresoMonto('');
        } else {
          setEgresoMonto('');
        }
      }
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Error al guardar el movimiento");
    }
  };

  return (
    <div className={style.container}>
      <p className={style.title}>Añadir ingreso / Añadir egreso</p>
      <p className={style.subTitle}>Fecha</p>
      <div className={style.containerAllForm}>

        {/* Formulario INGRESO */}
        <form
          className={style.formone}
          // Pasamos el estado 'ingresoMonto' al submit
          onSubmit={(e) => handleSubmit(e, "ingreso", ingresoMonto)}
        >
          {/* Reemplazamos el input viejo por nuestro nuevo componente */}
          <InputMonto
            className={style.input}
            value={ingresoMonto}
            onChange={setIngresoMonto}
          />
          <div className={style.containerBtn}>
            <input name="categoria" className={style.btn} placeholder="Categoría" required />
            <input name="fecha" className={style.date} type="date" defaultValue={fecha} />
          </div>
          <div className={style.containerDetalle}>
            <input name="detalle" className={style.detalle} placeholder="Detalle" />
            <button className={style.buttonSend} type="submit">Añadir ingreso</button>
          </div>
        </form>

        {/* Formulario EGRESO */}
        <form
          className={style.formtwo}
          // Pasamos el estado 'egresoMonto' al submit
          onSubmit={(e) => handleSubmit(e, "egreso", egresoMonto)}
        >
          {/* Reemplazamos el input viejo por nuestro nuevo componente */}
          <InputMonto
            className={style.input}
            value={egresoMonto}
            onChange={setEgresoMonto}
          />
          <div className={style.containerBtn}>
            <input name="categoria" className={style.btn} placeholder="Categoría" required />
            <input name="fecha" className={style.date} type="date" defaultValue={fecha} />
          </div>
          <div className={style.containerDetalle}>
            <input name="detalle" className={style.detalle} placeholder="Detalle" />
            <button className={style.buttonSend} type="submit">Añadir egreso</button>
          </div>
        </form>

      </div>
    </div>
  );
}

export default Add;
