import style from '../style/Results.module.css'
import { useEffect, useState } from 'react';
import axios from 'axios';
import LoginPage from './LoginPage';

function Results({ mostrarContenidoExtra }) {
  const [movimientos, setMovimientos] = useState([]);

  useEffect(() => {
    const fetchMovimientos = async () => {
      try {
        const res = await axios.get("http://localhost:3000/api/add");
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
      }
    };

    fetchMovimientos();
  }, []);

  // --- Función de ayuda para formatear el número como moneda ---
  const formatCurrency = (number) => {
    // Aseguramos que el valor sea un número
    const numericValue = Number(number) || 0;

    // Usamos Intl.NumberFormat para un formato de moneda preciso para Argentina
    return new Intl.NumberFormat('es-AR', {
      style: 'currency',
      currency: 'ARS',
      minimumFractionDigits: 2, // Mostramos siempre 2 decimales
    }).format(numericValue);
  };
  // -----------------------------------------------------------
  return (
    <div className={style.container}>
      <div>
        <h2>Movimientos</h2>

           {movimientos.length === 0 && <div>

              <p className={style.textInicio}>para comenzar inicie sesion o Registrate</p>

          </div>}
        {/* Usa la prop para mostrar/ocultar */}
        {mostrarContenidoExtra && (
          <div>
           <LoginPage/>
          </div>
        )}


        <div className={style.containerInfoAll}>
          {movimientos.map((mov) => (
            <div className={`${style.containerInfo} ${mov.tipo === 'ingreso' ? style.bordeIngreso : style.bordeEgreso
              }`} key={mov._id}>
              <div className={style.info}>
                <p  className={style.category}> {mov.categoria}</p> 
                <p className={style.detalle}> {mov.detalle} </p>
              </div>
              <p className={style.monto}> {formatCurrency(mov.monto)} </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  )
}

export default Results
