import { useState } from "react";
import api from '../api/axiosConfig'; // 游댠 Usar axios configurado
import style from '../style/Add.module.css';
import InputMonto from "./InputMonto";
import { useAuth } from '../context/AuthContext'

function Add() {
  const [fecha] = useState(new Date().toISOString().split("T")[0]);
  const [ingresoMonto, setIngresoMonto] = useState('');
  const [egresoMonto, setEgresoMonto] = useState('');
  const [loading, setLoading] = useState(false);
  const { user } = useAuth(); // 游댠 Obtener info del usuario autenticado

  const handleSubmit = async (e, tipo, monto) => {
    e.preventDefault();

    // 游댠 Verificar que el usuario est칠 autenticado
    if (!user) {
      alert("Debes iniciar sesi칩n para agregar movimientos");
      window.location.href = '/login';
      return;
    }

    const form = e.target;
    setLoading(true);

    const montoNumerico = parseFloat(monto);
    if (isNaN(montoNumerico) || montoNumerico <= 0) {
      alert("Por favor, ingresa un monto v치lido.");
      setLoading(false);
      return;
    }

    const data = {
      tipo,
      monto: montoNumerico,
      categoria: form.categoria.value,
      fecha: form.fecha.value,
      detalle: form.detalle.value
    };

    try {
      // 游댠 Cambiar la URL al endpoint correcto de tu backend
      const res = await api.post("/add", data);

      if (res.status === 201) {
        alert(`${tipo === "ingreso" ? "Ingreso" : "Egreso"} a침adido con 칠xito`);
        form.reset();

        if (tipo === "ingreso") {
          setIngresoMonto('');
        } else {
          setEgresoMonto('');
        }
      }
    } catch (err) {
      console.error("Error al guardar movimiento:", err);
      if (err.response?.status === 401) {
        alert("Sesi칩n expirada. Por favor, inicia sesi칩n nuevamente.");
        window.location.href = '/login';
      } else {
        alert(err.response?.data?.error || "Error al guardar el movimiento");
      }
    } finally {
      setLoading(false);
    }
  };

  // 游댠 Si no hay usuario, mostrar mensaje de login
  if (!user) {
    return (
      <div className={style.container}>
        <p className={style.title}>A침adir ingreso / A침adir egreso</p>
        <div className={style.loginMessage}>
          <p>Debes iniciar sesi칩n para gestionar tus movimientos</p>
          <button 
            onClick={() => window.location.href = '/login'}
            className={style.loginButton}
          >
            Iniciar Sesi칩n
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className={style.container}>
      <p className={style.title}>A침adir ingreso / A침adir egreso</p>
      <p className={style.subTitle}>Fecha</p>
      <div className={style.containerAllForm}>

        {/* Formulario INGRESO */}
        <form
          className={style.formone}
          onSubmit={(e) => handleSubmit(e, "ingreso", ingresoMonto)}
        >
          <InputMonto
            className={style.input}
            value={ingresoMonto}
            onChange={setIngresoMonto}
            disabled={loading}
          />
          <div className={style.containerBtn}>
            <input 
              name="categoria" 
              className={style.btn} 
              placeholder="Categor칤a" 
              required 
              disabled={loading}
            />
            <input 
              name="fecha" 
              className={style.date} 
              type="date" 
              defaultValue={fecha} 
              disabled={loading}
            />
          </div>
          <div className={style.containerDetalle}>
            <input 
              name="detalle" 
              className={style.detalle} 
              placeholder="Detalle" 
              disabled={loading}
            />
            <button 
              className={style.buttonSend} 
              type="submit" 
              disabled={loading}
            >
              {loading ? "Guardando..." : "A침adir ingreso"}
            </button>
          </div>
        </form>

        {/* Formulario EGRESO */}
        <form
          className={style.formtwo}
          onSubmit={(e) => handleSubmit(e, "egreso", egresoMonto)}
        >
          <InputMonto
            className={style.input}
            value={egresoMonto}
            onChange={setEgresoMonto}
            disabled={loading}
          />
          <div className={style.containerBtn}>
            <input 
              name="categoria" 
              className={style.btn} 
              placeholder="Categor칤a" 
              required 
              disabled={loading}
            />
            <input 
              name="fecha" 
              className={style.date} 
              type="date" 
              defaultValue={fecha} 
              disabled={loading}
            />
          </div>
          <div className={style.containerDetalle}>
            <input 
              name="detalle" 
              className={style.detalle} 
              placeholder="Detalle" 
              disabled={loading}
            />
            <button 
              className={style.buttonSend} 
              type="submit" 
              disabled={loading}
            >
              {loading ? "Guardando..." : "A침adir egreso"}
            </button>
          </div>
        </form>

      </div>
    </div>
  );
}

export default Add;
