import { useState } from 'react';
import axios from 'axios';
// Asumo que tienes un CSS para este componente, ajústalo si es necesario
import style from '../style/Add.module.css'; 

function Add({ onMovementAdded }) { // Recibe una función para refrescar la lista
  const [monto, setMonto] = useState('');
  const [detalle, setDetalle] = useState('');
  const [tipo, setTipo] = useState('ingreso'); // Para saber si es ingreso o egreso
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // 1. Leemos el token desde localStorage JUSTO antes de hacer la petición
    const token = localStorage.getItem("token");

    // Verificamos si el token existe por si acaso
    if (!token) {
      setError("No estás autorizado. Por favor, inicia sesión de nuevo.");
      return;
    }

    try {
      // 2. Hacemos la petición POST, pero ahora incluimos un tercer argumento:
      //    un objeto de configuración con los 'headers'.
      await axios.post(
        "http://localhost:3000/api/add", // O la ruta correcta para añadir
        {
          // El cuerpo de la petición con los datos del formulario
          monto: parseFloat(monto),
          detalle,
          tipo,
        },
        {
          // La configuración con el encabezado de autorización
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );

      // Limpiamos el formulario después de un envío exitoso
      setMonto('');
      setDetalle('');

      // Opcional pero recomendado: Llama a la función para refrescar la lista de movimientos
      if (onMovementAdded) {
        onMovementAdded();
      }

    } catch (err) {
      setError(err.response?.data?.error || "Error al añadir el movimiento.");
    }
  };

  return (
    <div className={style.container}>
      <h3>Añadir Movimiento</h3>
      <form onSubmit={handleSubmit} className={style.form}>
        <div>
          <label>Tipo:</label>
          <select value={tipo} onChange={(e) => setTipo(e.target.value)}>
            <option value="ingreso">Ingreso</option>
            <option value="egreso">Egreso</option>
          </select>
        </div>
        <div>
          <label htmlFor="monto">Monto:</label>
          <input
            id="monto"
            type="number"
            value={monto}
            onChange={(e) => setMonto(e.target.value)}
            placeholder="6.000"
            required
          />
        </div>
        <div>
          <label htmlFor="detalle">Detalle:</label>
          <input
            id="detalle"
            type="text"
            value={detalle}
            onChange={(e) => setDetalle(e.target.value)}
            placeholder="Trabajo"
            required
          />
        </div>
        <button type="submit" className={style.btn}>
          Añadir {tipo}
        </button>
        {error && <p className={style.error}>{error}</p>}
      </form>
    </div>
  );
}

export default Add;
