import { Outlet, useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import LeftSideNotas from "./LeftSideNotas";

function MainLayout({
  token,
  onLogout,
  taskToEdit,
  onTaskUpdate,
  refreshKey,
  onUpdate,
}) {
  const location = useLocation();

  // Estado global del panel de notas
  const [isNotesOpen, setIsNotesOpen] = useState(false);

  // Bloquear scroll cuando notas está abierto (mobile)
  useEffect(() => {
    if (window.innerWidth <= 1000) {
      document.body.style.overflow = isNotesOpen ? "hidden" : "";
    }

    return () => {
      document.body.style.overflow = "";
    };
  }, [isNotesOpen]);

  const sidebarMap = {
    "/notas": (
      <LeftSideNotas
        onTaskUpdate={onTaskUpdate}
        onUpdate={onUpdate}
        taskToEdit={taskToEdit}
        setIsNotesOpen={setIsNotesOpen}
      />
    ),
  };

  const DefaultSidebar = <LeftsSite token={token} refreshKey={refreshKey} />;
  const CurrentSidebar = sidebarMap[location.pathname];

  return (
    <div>
      <Nav token={token} onLogout={onLogout} />

      <main style={{ display: "flex" }}>
        {/* ❗️ NO renderizar sidebar cuando notas está abierto */}
        {token && !isNotesOpen && (CurrentSidebar || DefaultSidebar)}

        <div style={{ flex: 1 }}>
          <Outlet context={{ isNotesOpen }} />
        </div>
      </main>
    </div>
  );
}

export default MainLayout;
