// Results.js

import style from '../style/Results.module.css';
import { useEffect, useState } from 'react';
import axios from 'axios';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage'; 

// 2. RECIBIMOS LAS NUEVAS PROPS
function Results({ onLoginClick,  activeView, onCloseModal }) {
  const [movimientos, setMovimientos] = useState([]);

  useEffect(() => {
    const fetchMovimientos = async () => {
      try {
        const res = await axios.get("http://localhost:3000/api/add");
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
      }
    };
    fetchMovimientos();
  }, []);

  const formatCurrency = (number) => {
    const numericValue = Number(number) || 0;
    return new Intl.NumberFormat('es-AR', {
      style: 'currency', currency: 'ARS', minimumFractionDigits: 2,
    }).format(numericValue);
  };

  return (
    <div className={style.container}>
   <div>
        <h2>Movimientos</h2>

        {/* ==================================================================== */}
        {/* VISTA POR DEFECTO: Ahora muestra REGISTRO si no hay movimientos */}
        {/* ==================================================================== */}
        {movimientos.length === 0 && !activeView && (
          // Le pasamos la funci贸n para que desde Registro se pueda abrir el login
          <SignupPage onSwitchToLogin={onLoginClick} />
        )}

        {/* ==================================================================== */}
        {/* VISTA DE POP-UP: Ahora muestra LOGIN cuando el estado se activa */}
        {/* ==================================================================== */}
        {activeView === 'login' && (
          // A LoginPage le pasamos la funci贸n para que pueda CERRARSE
          <LoginPage onClose={onCloseModal}  />
        )}

        <div className={style.containerInfoAll}>
          {movimientos.map((mov) => (
            <div className={`${style.containerInfo} ${mov.tipo === 'ingreso' ? style.bordeIngreso : style.bordeEgreso}`} key={mov._id}>
              <div className={style.info}>
                <p className={style.category}>{mov.categoria}</p>
                <p className={style.detalle}>{mov.detalle}</p>
              </div>
              <p className={style.monto}>{formatCurrency(mov.monto)}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  )
}

export default Results;
