// App.js (CORREGIDO Y SEGURO)

import { useState, useEffect } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import axios from "axios";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
import style from "../style/App.module.css";

function MainLayout({ token, onAuthSuccess, onLoginClick, onCloseModal, activeView, onMovementAdded, movimientos }) {
  return (
    <div className={style.container}>
      <Nav />
      <div className={style.content}>
        {token ? (
          <>
            <LeftsSite movimientos={movimientos} />
            <div className={style.contentSide}>
              <Results token={token} movimientos={movimientos} />
              <Add token={token} onMovementAdded={onMovementAdded} />
            </div>
          </>
        ) : (
          <div className={style.authWrapper}>
            <Results
              token={token}
              onAuthSuccess={onAuthSuccess}
              onLoginClick={onLoginClick}
              onCloseModal={onCloseModal}
              activeView={activeView}
            />
          </div>
        )}
      </div>
    </div>
  );
}

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [activeView, setActiveView] = useState('signup');
  const [movimientos, setMovimientos] = useState([]);
  const [keyToRefresh, setKeyToRefresh] = useState(0);

  useEffect(() => {
    if (!token) {
      setMovimientos([]);
      return;
    }
    const fetchMovimientos = async () => {
      try {
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        if (err.response && (err.response.status === 401 || err.response.status === 403)) {
          localStorage.removeItem("token");
          setToken(null);
        }
      }
    };
    fetchMovimientos();
  }, [token, keyToRefresh]);

  // ✅ ARREGLO PRINCIPAL: Esta función ahora valida el token antes de guardarlo.
  const handleAuthSuccess = (newToken) => {
    // Verificamos que el token sea una cadena de texto válida y no vacía
    if (typeof newToken === 'string' && newToken.length > 0) {
      localStorage.setItem("token", newToken);
      setToken(newToken);
    } else {
      // Si el backend no envía un token correcto, no lo guardamos y avisamos en consola.
      console.error("Error: Se intentó guardar un token inválido:", newToken);
      alert("El servidor no respondió con un token válido. Intenta de nuevo.");
    }
  };

  const handleLoginClick = () => {
    setActiveView('login');
  };

  const handleCloseModal = () => {
    setActiveView('signup');
  };

  const refreshMovimientos = () => {
    setKeyToRefresh(prevKey => prevKey + 1);
  };

  return (
    <Routes>
      <Route
        path="/"
        element={
          <MainLayout
            token={token}
            movimientos={movimientos}
            onAuthSuccess={handleAuthSuccess}
            onLoginClick={handleLoginClick}
            onCloseModal={handleCloseModal}
            activeView={activeView}
            onMovementAdded={refreshMovimientos}
          />
        }
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
