import { useState, useEffect } from "react";
import AppRoutes from "./AppRoutes";
import { movimientoService } from "./api"; // Importamos el servicio centralizado

const getStoredToken = () =>
  localStorage.getItem("token") || sessionStorage.getItem("token");

function App() {
  const [token, setToken] = useState(getStoredToken); 
  const [ready, setReady] = useState(false); // Estado para evitar el parpadeo
  const [refreshKey, setRefreshKey] = useState(0);
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [movementToEdit, setMovementToEdit] = useState(null);
  const [movimientos, setMovimientos] = useState([]);

  // 1. EFECTO DE CARGA INICIAL (Crucial para el F5)
  useEffect(() => {
    const savedToken = getStoredToken();
    if (savedToken) {
      setToken(savedToken);
    }
    setReady(true); // Una vez que leímos el storage, la app está lista
  }, []);

  // 2. FETCH DE MOVIMIENTOS USANDO EL SERVICIO
  useEffect(() => {
    let isMounted = true;

    const fetchMovimientos = async () => {
      // Si no hay token o la app no está ready, no pedimos nada
      if (!token || !ready) {
        setMovimientos([]);
        return;
      }

      try {
        // Usamos el servicio (el interceptor en api.js ya pone el token solo)
        const res = await movimientoService.getAll(); 
        if (isMounted && res.data) {
          setMovimientos(res.data);
        }
      } catch (err) {
        console.error("Error en fetchMovimientos:", err.message);
      }
    };

    fetchMovimientos();
    return () => { isMounted = false; }; 
  }, [token, refreshKey, ready]); // Agregamos 'ready' como dependencia

  const handleDataUpdate = () => {
    setTaskToEdit(null);
    setMovementToEdit(null);
    setRefreshKey((prevKey) => prevKey + 1);
  };

  const handleAuthSuccess = (newToken) => {
    setToken(newToken);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    sessionStorage.removeItem("token");
    setToken(null);
  };

  // Si no está ready, mostramos una pantalla de carga para que no intente ir al Login
  if (!ready) return <div className="spinner">Cargando...</div>;

  return (
    <AppRoutes
      token={token}
      onAuthSuccess={handleAuthSuccess}
      onLogout={handleLogout}
      refreshKey={refreshKey}
      onUpdate={handleDataUpdate}
      movimientos={movimientos}
      movementToEdit={movementToEdit}
      setMovementToEdit={setMovementToEdit}
      taskToEdit={taskToEdit}
      setTaskToEdit={setTaskToEdit}
    />
  );
}

export default App;
