//import { useState } from 'react';
import Results from './Results';
import Add from './Add';
import style from '../style/App.module.css';
import { useState } from 'react';

function Dashboard({ 
  token, 
  movimientos, 
  refreshKey, 
  onMovementUpdate, 
  movementToEdit, 
  setMovementToEdit, 

  ...authProps 
}) {
  // const navigate removed: no pantalla completa button desired
  const [showOnly, setShowOnly] = useState(null); // null | 'ingreso' | 'egreso'
  const [selectedType, setSelectedType] = useState('ingreso'); // switch control
  // --- NUEVO ESTADO: controlar si se ven todos los movimientos ---
  //const [showAllMovimientos, setShowAllMovimientos] = useState(false);

  // --- FUNCIONES QUE PASAMOS A RESULTS ---
  //const handleShowAllChange = (showAll) => {
   // setShowAllMovimientos(showAll);
  //};

 // const handleEditClick = (mov) => {
   // setMovementToEdit(mov);
    //setShowAllMovimientos(false); // opcional: esconder tabla al editar
  //};



  return (
    <div className={style.contentSide}>
      {token && (
        <div style={{ display: 'flex', gap: '0.75rem', alignItems: 'center', justifyContent: 'flex-end', marginBottom: '0.5rem' }}>
          {!showOnly && (
            <>
              <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <input
                  type="checkbox"
                  checked={selectedType === 'egreso'}
                  onChange={() => setSelectedType(prev => prev === 'ingreso' ? 'egreso' : 'ingreso')}
                />
                <span>{selectedType === 'ingreso' ? 'Ingreso' : 'Egreso'}</span>
              </label>
              <button onClick={() => setShowOnly(selectedType)} className={style.buttonSend}>
                Abrir
              </button>
            </>
          )}
          {showOnly && (
            <button onClick={() => setShowOnly(null)} className={style.buttonSend}>
              Cerrar
            </button>
          )}
        </div>
      )}
      {!showOnly && (
        <Results
          token={token} 
          movimientos={movimientos}
          refreshKey={refreshKey}
          //onEditClick={handleEditClick}
          onMovementUpdate={onMovementUpdate}
         // onShowAllChange={handleShowAllChange} // <-- NUEVO PROP
          {...authProps}
        />
      )}
      {/* Mostrar el componente Add en la p√°gina si showOnly !== undefined */}
      {showOnly && (
        <div style={{ marginTop: '1rem' }}>
          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '0.5rem', marginBottom: '0.5rem' }}>
            <button onClick={() => setShowOnly(null)} className={style.buttonSend}>
              Cerrar
            </button>
          </div>
          <Add
            onMovementAdded={() => { onMovementUpdate(); setShowOnly(null); }}
            movementToEdit={movementToEdit}
            only={showOnly}
          />
        </div>
      )}
    </div>
  );
}

export default Dashboard;
