import style from '../style/Results.module.css'
import { useEffect, useState } from 'react';
import axios from 'axios';

function Results() {
  const [movimientos, setMovimientos] = useState([]);

  useEffect(() => {
    const fetchMovimientos = async () => {
      try {
        const res = await axios.get("http://localhost:3000/api/add");
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
      }
    };

    fetchMovimientos();
  }, []);
  return (
    <div className={style.container}>
           <div>
      <h2>Movimientos</h2>
      {movimientos.length === 0 && <p>No hay movimientos</p>}
      <ul>
        {movimientos.map((mov) => (
          <li key={mov._id}>
            <strong>{mov.tipo.toUpperCase()}</strong> - {mov.categoria} - ${mov.monto} - {new Date(mov.fecha).toLocaleDateString()} - {mov.detalle}
          </li>
        ))}
      </ul>
    </div>
    </div>
  )
}

export default Results
