// Results.js (Versión final con autenticación)

import { useEffect, useState } from 'react';
import axios from 'axios';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import style from '../style/Results.module.css';

function Results({ onLoginClick, activeView, onCloseModal, token, onAuthSuccess }) {
  const [movimientos, setMovimientos] = useState([]);

  useEffect(() => {
    const fetchMovimientos = async () => {
      try {
        // 1. LA PETICIÓN AHORA ENVÍA EL TOKEN DE AUTORIZACIÓN
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        // Aquí podrías manejar errores de token inválido, por ejemplo, haciendo logout.
      }
    };

    // 2. SOLO BUSCA LOS MOVIMIENTOS SI EXISTE UN TOKEN
    if (token) {
      fetchMovimientos();
    }
  }, [token]); // El useEffect se vuelve a ejecutar si el token cambia

  const formatCurrency = (number) => { /* ... */ };

  // 3. LÓGICA DE RENDERIZADO PRINCIPAL
  // Si NO hay token, muestra la vista de autenticación.
  if (!token) {
    return (
      <div>
        {/* Muestra Registro por defecto, o el pop-up de Login si está activo */}
        {!activeView && <SignupPage onSwitchToLogin={onLoginClick} onAuthSuccess={onAuthSuccess} />}
        {activeView === 'login' && <LoginPage onClose={onCloseModal} onAuthSuccess={onAuthSuccess} />}
      </div>
    );
  }

  // Si SÍ hay token, muestra la lista de movimientos.
  return (
    <div className={style.container}>
      <div>
        <h2>Movimientos</h2>
        <div className={style.containerInfoAll}>
          {movimientos.length === 0 && <p>Aún no tienes movimientos. ¡Añade uno!</p>}
          {movimientos.map((mov) => (
            <div className={`${style.containerInfo} ${mov.tipo === 'ingreso' ? style.bordeIngreso : style.bordeEgreso}`} key={mov._id}>
              <div className={style.info}>
                <p className={style.category}>{mov.categoria}</p>
                <p className={style.detalle}>{mov.detalle}</p>
              </div>
              <p className={style.monto}>{formatCurrency(mov.monto)}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Results;
