import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import style from '../style/App.module.css';
import Nav from './Nav';
import LeftsSite from './LeftsSite';
import Add from './Add';
import Results from './Results';
import Login from './Login';
import Register from './Register';

// Componente protegido - solo para usuarios autenticados
const ProtectedRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) return <div className={style.loading}>Cargando...</div>;
  return user ? children : <Navigate to="/login" />;
};

// Layout principal (dashboard) que requiere autenticación
const DashboardLayout = () => {
  return (
    <div className={style.container}>
      <Nav />
      <div className={style.content}>
        <LeftsSite />
        <div className={style.contentSide}>
          <Results />
          <Add />
        </div>
      </div>
    </div>
  );
};

// Layout público (sin navbar) para login/register
const PublicLayout = ({ children }) => {
  return (
    <div>
      {children}
    </div>
  );
};

function App() {
  return (
    <Router>
      <Routes>
        {/* Rutas públicas */}
        <Route 
          path="/login" 
          element={
            <PublicLayout>
              <Login />
            </PublicLayout>
          } 
        />

        <Route 
          path="/register" 
          element={
            <PublicLayout>
              <Register />
            </PublicLayout>
          } 
        />

        {/* Ruta protegida (dashboard) */}
        <Route 
          path="/" 
          element={
            <ProtectedRoute>
              <DashboardLayout />
            </ProtectedRoute>
          } 
        />

        {/* Redirección para rutas no encontradas */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
