// src/MainLayout.jsx (VERSIÓN FINAL)

import React from 'react';
import { Outlet, useLocation } from 'react-router-dom';
import Nav from './Nav';
import LeftsSite from './LeftsSite';
import LeftSideNotas from './LeftSideNotas';

// Recibe onTaskUpdate (en lugar de onTaskAdded) para mayor claridad
function MainLayout({ token, onLogout, taskToEdit, onTaskUpdate }) {
  const location = useLocation();

  const sidebarMap = {
    // Pasamos la prop con el nombre que espera LeftSideNotas
    '/notas': <LeftSideNotas onTaskAdded={onTaskUpdate} taskToEdit={taskToEdit} />,
  };

  const DefaultSidebar = <LeftsSite />;
  const CurrentSidebar = sidebarMap[location.pathname];

  return (
    <div>
      <Nav token={token} onLogout={onLogout} />
      <main style={{ display: 'flex' }}>
        {CurrentSidebar || DefaultSidebar}
        <Outlet />
      </main>
    </div>
  );
}

export default MainLayout;
