import { useState } from "react";
import axios from "axios";
import style from '../style/Add.module.css';

function Add() {
  const [fecha] = useState(new Date().toISOString().split("T")[0]); // formato YYYY-MM-DD

    const [valorFormateado, setValorFormateado] = useState('');
  const [valorReal, setValorReal] = useState(0);

  const manejarCambio = (e) => {
    const valor = e.target.value;

    // Eliminar todo excepto números y coma decimal
    const soloNumeros = valor.replace(/[^\d,]/g, '');

    // Reemplazar múltiples comas por una sola
    const unaComa = soloNumeros.replace(/,+/g, ',');

    // Separar parte entera y decimal
    const partes = unaComa.split(',');
    let parteEntera = partes[0] || '';
    const parteDecimal = partes[1] || '';

    // Aplicar separadores de miles a la parte entera
    parteEntera = parteEntera.replace(/\B(?=(\d{3})+(?!\d))/g, '.');

    // Construir el nuevo valor formateado
    let nuevoValor = parteEntera;
    if (parteDecimal) {
      nuevoValor += `,${parteDecimal.substring(0, 2)}`; // Limitar a 2 decimales
    }

    setValorFormateado(nuevoValor);

    // Convertir a número para guardar el valor real (reemplazar puntos y comas)
    const valorNumerico = parseFloat(
      nuevoValor.replace(/\./g, '').replace(',', '.')
    ) || 0;

    setValorReal(valorNumerico);
  };


  const handleSubmit = async (e, tipo) => {
    e.preventDefault();
    const form = e.target;

    const data = {
      tipo, // "ingreso" o "egreso"
      monto: form.monto.value,
      categoria: form.categoria.value,
      fecha: form.fecha.value,
      detalle: form.detalle.value
    };

    try {
      const res = await axios.post("http://localhost:3000/api/add", data);

      if (res.status === 201) {
        alert(`${tipo} añadido con éxito`);
        form.reset();
      }
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "Error al guardar el movimiento");
    }
  };

  return (
    <div className={style.container}>
      <p className={style.title}>Añadir ingreso / Añadir egreso</p>
      <p className={style.subTitle}>Fecha</p>
      <div className={style.containerAllForm}>

        {/* Formulario INGRESO */}
        <form
          className={style.formone}
          onSubmit={(e) => handleSubmit(e, "ingreso")}
        >
          <input name="monto" className={style.input} type="text"  placeholder="Ingresar monto" inputMode="decimal"  value={valorFormateado}
        onChange={manejarCambio} required />
          <div className={style.containerBtn}>
            <input name="categoria" className={style.btn} placeholder="Categoría" required />
            <input name="fecha" className={style.date} type="date" defaultValue={fecha} />
          </div>
          <div className={style.containerDetalle}>
            <input name="detalle" className={style.detalle} placeholder="Detalle" />
            <button className={style.buttonSend} type="submit">Añadir ingreso</button>
          </div>
        </form>

        {/* Formulario EGRESO */}
        <form
          className={style.formtwo}
          onSubmit={(e) => handleSubmit(e, "egreso")}
        >
          <input name="monto" className={style.input} type="number" placeholder="Ingresar monto" required />
          <div className={style.containerBtn}>
            <input name="categoria" className={style.btn} placeholder="Categoría" required />
            <input name="fecha" className={style.date} type="date" defaultValue={fecha} />
          </div>
          <div className={style.containerDetalle}>
            <input name="detalle" className={style.detalle} placeholder="Detalle" />
            <button className={style.buttonSend} type="submit">Añadir egreso</button>
          </div>
        </form>

      </div>
    </div>
  );
}

export default Add;
