import { useState, useMemo } from "react";
import axios from "axios";
import LoginPage from "./LoginPage";
import SignupPage from "./SignupPage";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import style from "../style/Results.module.css";
import { forwardRef } from "react";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:3000";

function Results({
  token,
  onAuthSuccess,
  onLoginClick,
  onCloseModal,
  activeView,
  onEditClick,
  movimientos,
  onMovementUpdate,
  onShowAllChange,
}) {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showAll, setShowAll] = useState(false);

  // --- FILTRADO DE MOVIMIENTOS ---
  const filteredMovimientos = useMemo(() => {
    if (!Array.isArray(movimientos)) return [];
    if (showAll) return movimientos;

    const localSelectedDate = new Date(selectedDate);
    localSelectedDate.setMinutes(
      localSelectedDate.getMinutes() - localSelectedDate.getTimezoneOffset()
    );
    const formattedSelectedDate = localSelectedDate.toISOString().slice(0, 10);

    return movimientos.filter(
      (mov) => mov.fecha.slice(0, 10) === formattedSelectedDate
    );
  }, [movimientos, selectedDate, showAll]);

  // --- MANEJADORES ---
  const handleDateChange = (date) => {
    setSelectedDate(date);
    setShowAll(false);
    if (onShowAllChange) onShowAllChange(false);
  };

  const handleAllMovimientos = async () => {
    try {
      const res = await axios.get(`${API_URL}/api/add/all`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.data) {
        if (onMovementUpdate) onMovementUpdate(res.data);
        setShowAll(true);
        if (onShowAllChange) onShowAllChange(true);
      }
    } catch (err) {
      console.error("Error al obtener todos los movimientos:", err);
    }
  };

  const handleDeleteMovimiento = async (movimientoId) => {
    if (!window.confirm("¿Estás seguro de que quieres eliminar este movimiento?")) return;
    try {
      await axios.delete(`${API_URL}/api/add/${movimientoId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (onMovementUpdate) onMovementUpdate();
    } catch (err) {
      console.error("Error al eliminar el movimiento:", err);
    }
  };

  const CalendarButton = forwardRef(({ onClick }, ref) => (
    <button type="button" className={style.calendarButton} onClick={onClick} ref={ref}>
      <img src="./calendario.png" alt="calendario" />
    </button>
  ));

  const handleToday = () => {
    setSelectedDate(new Date());
    setShowAll(false);
    if (onShowAllChange) onShowAllChange(false);
  };

  if (!token) {
    return (
      <div>
        {activeView === "login" ? (
          <LoginPage onClose={onCloseModal} onAuthSuccess={onAuthSuccess} />
        ) : (
          <SignupPage onSwitchToLogin={onLoginClick} onAuthSuccess={onAuthSuccess} />
        )}
      </div>
    );
  }

  // Decidimos qué lista mostrar (Historial o Filtrados)
  const displayList = showAll ? movimientos : filteredMovimientos;

  return (
    <div className={style.container}>
      {/* TU HEADER ORIGINAL INTACTO */}
      <div className={style.header}>
        <div className={style.datePickerContainer}>
          <button className={style.icon}>
            <DatePicker
              selected={selectedDate}
              onChange={handleDateChange}
              dateFormat="dd-MM-yyyy"
              customInput={<CalendarButton />}
              popperPortalId="root"
              popperPlacement="bottom-start"
            />
            <p>Calendario</p>
          </button>

          <button onClick={handleToday} className={style.icon}>
            <img src="./hoy.png" alt="hoy" />
            <p>Hoy</p>
          </button>

          <button onClick={handleAllMovimientos} className={style.icon}>
            <img src="./historial.png" alt="historial" />
            <p>Historial</p>
          </button>
        </div>
      </div>

      {/* --- CONTENEDOR DE RESULTADOS EN LISTA DE 3 COLUMNAS --- */}
      <div className={style.movimientosListUnificado}>
        {displayList.length === 0 ? (
          <p style={{ textAlign: "center", marginTop: "2rem", color: "var(--color-text)" }}>
            No hay movimientos para mostrar.
          </p>
        ) : (
          displayList.map((mov) => (
            <div
              key={mov._id}
              className={`${style.movimientoRowUnificado} ${
                mov.tipo === "ingreso" ? style.bordeIngreso : style.bordeEgreso
              }`}
            >
              {/* COLUMNA 1: FECHA Y CATEGORÍA */}
              <div className={style.columnaInfo}>
                <span className={style.fechaTexto}>{new Date(mov.fecha).toLocaleDateString()}</span>
                <p className={style.categoriaTexto}>{mov.categoria}</p>
                <p className={style.detalleTexto}>{mov.detalle}</p>
              </div>

              {/* COLUMNA 2: MONTO */}
              <div className={style.columnaMonto}>
                <p className={`${style.montoEstilo} ${mov.tipo === "ingreso" ? style.montoIngreso : style.montoEgreso}`}>
                  {mov.tipo === "ingreso" ? "+" : "-"} ${mov.monto}
                </p>
              </div>

              {/* COLUMNA 3: ACCIONES */}
              <div className={style.columnaAcciones}>
                <button onClick={() => onEditClick(mov)} className={style.botonAccion}>
                  <img src="/edit.png" alt="edit" />
                </button>
                <button onClick={() => handleDeleteMovimiento(mov._id)} className={style.botonAccion}>
                  <img src="/trush.png" alt="delete" />
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Results;
