// App.js

import { useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
import style from "../style/App.module.css";

function PrivateRoute({ children }) { return children; }

function PrivateLayout({ token, onAuthSuccess, onLoginClick, onCloseModal, activeView }) {
  return (
    <div className={style.container}>
      <Nav />
      <div className={style.content}>
        <LeftsSite />
        <div className={style.contentSide}>
          <Results
            token={token}
            onAuthSuccess={onAuthSuccess}
            onLoginClick={onLoginClick}
            onCloseModal={onCloseModal}
            activeView={activeView}
          />
          <Add />
        </div>
      </div>
    </div>
  );
}

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [activeView, setActiveView] = useState(null);

  const handleAuthSuccess = (newToken) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
    setActiveView(null); // Cierra cualquier pop-up después del éxito
  };

  const handleLoginClick = () => {
    setActiveView('login');
  };

  const handleCloseModal = () => {
    setActiveView(null);
  };

  return (
    <Routes>
      <Route
        path="/"
        element={
          <PrivateRoute>
            <PrivateLayout
              token={token}
              onAuthSuccess={handleAuthSuccess}
              onLoginClick={handleLoginClick}
              onCloseModal={handleCloseModal}
              activeView={activeView}
            />
          </PrivateRoute>
        }
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
