// App.js

import { useState, useEffect } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import axios from "axios";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
import style from "../style/App.module.css";

function MainLayout({ token, onAuthSuccess, onLoginClick, onCloseModal, activeView, onMovementAdded, movimientos }) {
  return (
    <div className={style.container}>
      <Nav />
      <div className={style.content}>
        {/* Lógica condicional: Muestra el layout principal o solo el de autenticación */}
        {token ? (
          <>
            <LeftsSite movimientos={movimientos} />
            <div className={style.contentSide}>
              <Results token={token} movimientos={movimientos} />
              <Add token={token} onMovementAdded={onMovementAdded} />
            </div>
          </>
        ) : (
          // Si no hay token, centramos los formularios de login/registro
          <div className={style.authWrapper}>
            <Results
              token={token}
              onAuthSuccess={onAuthSuccess}
              onLoginClick={onLoginClick}
              onCloseModal={onCloseModal}
              activeView={activeView}
            />
          </div>
        )}
      </div>
    </div>
  );
}

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [activeView, setActiveView] = useState('signup'); // Empezamos en la vista de registro
  const [movimientos, setMovimientos] = useState([]);
  const [keyToRefresh, setKeyToRefresh] = useState(0); // Un truco para forzar la recarga

  // Ahora App.js busca los movimientos
  useEffect(() => {
    if (!token) {
      setMovimientos([]);
      return;
    }
    const fetchMovimientos = async () => {
      try {
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        if (err.response && (err.response.status === 401 || err.response.status === 403)) {
          // Si el token es inválido, cerramos sesión
          localStorage.removeItem("token");
          setToken(null);
        }
      }
    };
    fetchMovimientos();
  }, [token, keyToRefresh]); // Se ejecuta si el token cambia o si forzamos la actualización

  const handleAuthSuccess = (newToken) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
  };

  const handleLoginClick = () => {
    setActiveView('login');
  };

  const handleCloseModal = () => {
    setActiveView('signup'); // Vuelve a la vista de registro
  };

  // Esta función se la pasaremos a 'Add' para que nos avise cuándo refrescar
  const refreshMovimientos = () => {
    setKeyToRefresh(prevKey => prevKey + 1);
  };

  return (
    <Routes>
      <Route
        path="/"
        element={
          <MainLayout
            token={token}
            movimientos={movimientos}
            onAuthSuccess={handleAuthSuccess}
            onLoginClick={handleLoginClick}
            onCloseModal={handleCloseModal}
            activeView={activeView}
            onMovementAdded={refreshMovimientos}
          />
        }
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
