import { useAuth } from '../context/AuthContext';
import style from '../style/Nav.module.css';

function Nav() {
  const { user, logout } = useAuth();

  const handleLogout = () => {
    logout();
    window.location.href = '/login';
  };

  return (
    <div className={style.container}>
      <nav className={style.nav}>
        <div className={style.containerLogo}>
          <img className={style.logo} src="./logo.png" alt="logo" />
          <p className={style.nameLogo}>growth</p>
        </div>

        <div className={style.navItems}>
          <div className={style.navItemHome}>
            <img className={style.imgItem} src="./home.png" alt="home" />
            <p className={style.textItem}>Home</p>
          </div>

          <div className={style.containerImg}>
            <img src="./notes.png" alt="notes" />
            <img src="./tasklist.png" alt="tasklist" />
            <img src="./help.png" alt="help"/>
            <img src="./settings.png" alt="settings" />
          </div>

          <div className={style.user}>
            {user ? (
              <>
                <p>Hola, {user.email || 'Usuario'}!</p>
                <button onClick={handleLogout} className={style.logoutBtn}>
                  Cerrar sesión
                </button>
              </>
            ) : (
              <>
                <p>Hola, Invitado!</p>
                <a href="/login">Iniciar sesión</a>
              </>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
}

export default Nav;
