// Results.js

import { useEffect, useState } from 'react';
import axios from 'axios';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import style from '../style/Results.module.css';

function Results({ token, onAuthSuccess, onLoginClick, onCloseModal, activeView }) {
  const [movimientos, setMovimientos] = useState([]);

  useEffect(() => {
    const fetchMovimientos = async () => {
      if (!token) {
        setMovimientos([]); // Si no hay token, la lista de movimientos está vacía
        return;
      }
      try {
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        setMovimientos([]); // Si el token es malo, vaciamos la lista
      }
    };
    fetchMovimientos();
  }, [token]);

  // --- RENDERIZADO CONDICIONAL BASADO EN EL TOKEN ---

  // Si NO hay token, el usuario no está logueado. Mostramos la vista de autenticación.
  if (!token) {
    return (
      <div>
        {/* La vista por defecto es Registro. Si se activa el pop-up, se muestra Login. */}
        {activeView === 'login' ? (
          <LoginPage 
            onClose={onCloseModal} 
            onAuthSuccess={onAuthSuccess} 
          />
        ) : (
          <SignupPage 
            onSwitchToLogin={onLoginClick} 
            onAuthSuccess={onAuthSuccess} 
          />
        )}
      </div>
    );
  }

  // Si SÍ hay un token, el usuario está logueado. Mostramos la vista de movimientos.
  return (
    <div className={style.container}>
      <div>
        <h2>Movimientos</h2>
        <div className={style.containerInfoAll}>
          {movimientos.length === 0 ? (
            <p style={{ textAlign: 'center', marginTop: '2rem' }}>
              Aún no tienes movimientos. ¡Añade uno para comenzar!
            </p>
          ) : (
            movimientos.map((mov) => (
              <div className={`${style.containerInfo} ${mov.tipo === 'ingreso' ? style.bordeIngreso : style.bordeEgreso}`} key={mov._id}>
                <div className={style.info}>
                  <p className={style.category}>{mov.categoria}</p>
                  <p className={style.detalle}>{mov.detalle}</p>
                </div>
                <p className={style.monto}>{mov.monto}</p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default Results;
