// src/api/api.js
import axios from 'axios';

// Creamos una instancia de Axios con una configuraci贸n base
const apiClient = axios.create({
  baseURL: 'http://localhost:3000/api', // URL base para todas las peticiones
});

//  Usamos un "interceptor" para modificar las peticiones ANTES de que se env铆en
apiClient.interceptors.request.use(
  (config) => {
    // 1. Obtenemos el token guardado en el navegador (localStorage)
    const token = localStorage.getItem('token');

    // 2. Si el token existe, lo a帽adimos a los headers de la petici贸n
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }

    // 3. Devolvemos la configuraci贸n modificada
    return config;
  },
  (error) => {
    // Manejamos errores en la configuraci贸n de la petici贸n
    return Promise.reject(error);
  }
);

export default apiClient;
