// src/api/api.js
import axios from 'axios';

// Creamos una instancia de Axios con una configuración base
const apiClient = axios.create({
  baseURL: 'http://localhost:3000/api', // URL base para todas las peticiones
});

// 🔥 Usamos un "interceptor" para modificar las peticiones ANTES de que se envíen
apiClient.interceptors.request.use(
  (config) => {
    // 1. Obtenemos el token guardado en el navegador (localStorage)
    const token = localStorage.getItem('token');

    // 2. Si el token existe, lo añadimos a los headers de la petición
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }

    // 3. Devolvemos la configuración modificada
    return config;
  },
  (error) => {
    // Manejamos errores en la configuración de la petición
    return Promise.reject(error);
  }
);

export default apiClient;
