import { useState, useEffect } from "react";
import axios from "axios";
import AppRoutes from "./AppRoutes";
// Esto atrapa cualquier error 401 en TODA la app
axios.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response && error.response.status === 401) {
      localStorage.removeItem("token");
      sessionStorage.removeItem("token");
      window.location.href = "/"; // Redirige al inicio/login
    }
    return Promise.reject(error);
  }
);
const getStoredToken = () =>
  localStorage.getItem("token") || sessionStorage.getItem("token");

function App() {
  const [token, setToken] = useState(getStoredToken());
  const [activeView, setActiveView] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  const [taskToEdit, setTaskToEdit] = useState(null);
  const [movementToEdit, setMovementToEdit] = useState(null);
  const [movimientos, setMovimientos] = useState([]);

  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:3000";
useEffect(() => {
  const fetchMovimientos = async () => {
    if (!token) {
      setMovimientos([]);
      return;
    }
    try {
      const res = await axios.get(`${API_URL}/api/add`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      // SOLO si la respuesta tiene datos, actualizamos
      if (res.data) {
        setMovimientos(res.data);
      }
    } catch (err) {
      console.error("Error en fetch:", err);
      // NO hagas setMovimientos([]) aquí a menos que sea error 401
      // El interceptor de arriba se encargará del 401
    }
  };
  fetchMovimientos();
}, [token, refreshKey, API_URL]);

  useEffect(() => {
    const syncToken = () => setToken(getStoredToken());

    window.addEventListener("storage", syncToken);
    window.addEventListener("focus", syncToken);

    return () => {
      window.removeEventListener("storage", syncToken);
      window.removeEventListener("focus", syncToken);
    };
  }, []);

  const handleDataUpdate = () => {
    setTaskToEdit(null);
    setMovementToEdit(null);
    setRefreshKey((prevKey) => prevKey + 1);
  };

  const handleAuthSuccess = (newToken) => {
    // ❌ ya NO guardar acá
    setToken(newToken);
    setActiveView(null);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    sessionStorage.removeItem("token");
    setToken(null);
  };

  const handleLoginClick = () => setActiveView("login");
  const handleCloseModal = () => setActiveView(null);

  const handleAddMovement = () => {
    setMovementToEdit(null);
    setActiveView(null);
  };

  return (
    <AppRoutes
      token={token}
      onAuthSuccess={handleAuthSuccess}
      onLogout={handleLogout}
      onLoginClick={handleLoginClick}
      onCloseModal={handleCloseModal}
      activeView={activeView}
      refreshKey={refreshKey}
      onUpdate={handleDataUpdate}
      movimientos={movimientos}
      movementToEdit={movementToEdit}
      setMovementToEdit={setMovementToEdit}
      taskToEdit={taskToEdit}
      setTaskToEdit={setTaskToEdit}
      onAddMovement={handleAddMovement}
    />
  );
}

export default App;
