import axios from "axios";
import { useState, useEffect } from "react";
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import style from '../style/Tarea.module.css';
import resultsStyle from '../style/Results.module.css';

// Mover la constante fuera del componente para optimizar
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

function Tareas({ token, refreshKey, onEditClick }) {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [allTasks, setAllTasks] = useState([]);
  const [loadingAll, setLoadingAll] = useState(false);
  const [allError, setAllError] = useState('');
  const [error, setError] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showAll, setShowAll] = useState(false);

  useEffect(() => {
    const fetchTasks = async () => {
      if (!token) { setTasks([]); return; }
      setLoading(true);
      setError('');
      try {
        const dateToFetch = new Date(selectedDate);
        dateToFetch.setMinutes(dateToFetch.getMinutes() - dateToFetch.getTimezoneOffset());
        const formattedDate = dateToFetch.toISOString().slice(0, 10);
        const res = await axios.get(`${API_URL}/api/task?fecha=${formattedDate}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTasks(res.data);
      } catch (err) {
        console.error("Error al obtener las tareas:", err);
        setError("No se pudieron cargar las tareas para esta fecha.");
        setTasks([]);
      } finally {
        setLoading(false);
      }
    };
    fetchTasks();
  }, [token, refreshKey, selectedDate]);

  // Fetch all tasks when showAll is true
  useEffect(() => {
    const fetchAll = async () => {
      if (!token) { setAllTasks([]); return; }
      if (!showAll) return;
      setLoadingAll(true);
      setAllError('');
      try {
        // Intentamos primero la opción explícita ?all=true (si el backend la soporta)
        let res;
        try {
          res = await axios.get(`${API_URL}/api/task?all=true`, {
            headers: { Authorization: `Bearer ${token}` }
          });
        } catch (err) {
          // Si falla, intentamos sin query (algunas versiones devuelven todas sin fecha)
          res = await axios.get(`${API_URL}/api/task`, {
            headers: { Authorization: `Bearer ${token}` }
          });
        }
        setAllTasks(res.data);
      } catch (err) {
        console.error('Error al obtener todas las tareas:', err);
        setAllError('No se pudieron cargar todas las tareas. Verifica el backend.');
        setAllTasks([]);
      } finally {
        setLoadingAll(false);
      }
    };
    fetchAll();
  }, [token, refreshKey, showAll]);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const renderContent = () => {
    if (loading) return <p>Cargando tareas...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;
    if (tasks.length === 0) return <p>Aún no tienes tareas. ¡Añade una para comenzar!</p>;

    const handleToggleComplete = async (taskId, currentStatus) => {
      try {
        // NOTA: Asegúrate que esta ruta exista en tu backend para evitar conflictos
        const response = await axios.put(`${API_URL}/api/task/${taskId}/status`,
          { completada: !currentStatus },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setTasks(prevTasks =>
          prevTasks.map(task =>
            task._id === taskId ? response.data : task
          )
        );
      } catch (err) {
        console.error("Error al actualizar la tarea:", err);
      }
    };

    const handleDeleteTask = async (taskId) => {
      if (!window.confirm("¿Estás seguro de que quieres eliminar esta tarea?")) return;
      try {
        await axios.delete(`${API_URL}/api/task/${taskId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTasks(prevTasks => prevTasks.filter(task => task._id !== taskId));
      } catch (err) {
        console.error("Error al eliminar la tarea:", err);
      }
    };

    return tasks.map((task) => (
      <div className={style.taskContainer} key={task._id}>
        <div className={`${style.taskCard} ${task.completada ? style.completed : ''} ${style[task.color] || style.color1}`}>
          <div className={style.taskHeader}>
            <p className={style.taskMeta}>{task.meta}</p>
            <p className={style.taskUrgency}>Urgencia &gt; {task.urgencia}</p>
          </div>
          <div className={style.taskDetails}>
            <p>Horario: {task.horario || 'Sin horario'}</p>
          </div>
          <div className={style.taskActions}>
            <p className={task.completada ? style.statusDone : style.statusPending}>
              {task.completada ? 'Hecho' : 'Pendiente'}
            </p>
            <input
              type="checkbox"
              checked={task.completada}
              onChange={() => handleToggleComplete(task._id, task.completada)}
            />
            <button
              onClick={() => onEditClick(task)}
              className={style.editButton}
            >
              <img className={style.ButtonImg} src="/edit.png" alt="edit" />
            </button>
            <button
              onClick={() => handleDeleteTask(task._id)}
              className={style.deleteButton}
            >
              <img className={style.ButtonImg} src="/trush.png" alt="delete" />
            </button>
          </div>
        </div>
      </div>
    ));
  };

  const renderAllTasksTable = () => {
    if (loadingAll) return <p>Cargando todas las tareas...</p>;
    if (allError) return <p style={{ color: 'red' }}>{allError}</p>;
    if (!allTasks || allTasks.length === 0) return <p style={{ textAlign: 'center' }}>No hay tareas guardadas.</p>;

    const handleDeleteTask = async (taskId) => {
      if (!window.confirm("¿Estás seguro de que quieres eliminar esta tarea?")) return;
      try {
        await axios.delete(`${API_URL}/api/task/${taskId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setAllTasks(prev => prev.filter(t => t._id !== taskId));
      } catch (err) {
        console.error("Error al eliminar la tarea:", err);
      }
    };

    return (
      <div>
        <table className={resultsStyle.movimientosTable}>
          <thead>
            <tr>
              <th>Fecha</th>
              <th>Meta</th>
              <th>Horario</th>
              <th>Urgencia</th>
              <th>Estado</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {allTasks.map(t => (
              <tr key={t._id}>
                <td>{t.fecha ? t.fecha.slice(0,10) : '-'}</td>
                <td>{t.meta}</td>
                <td>{t.horario || '-'}</td>
                <td>{t.urgencia}</td>
                <td>{t.completada ? 'Hecho' : 'Pendiente'}</td>
                <td>
                  <button onClick={() => onEditClick(t)} style={{ marginRight: 8 }}>Editar</button>
                  <button onClick={() => handleDeleteTask(t._id)}>Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className={style.container}>
      <div className={style.header}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <h1>{showAll ? 'Todas las tareas' : 'Mis Tareas'}</h1>
          {!showAll && (
            <DatePicker
              selected={selectedDate}
              onChange={handleDateChange}
              dateFormat="dd-MM-yyyy"
              className={style.datePicker}
            />
          )}
        </div>

        <div style={{ display: 'flex', gap: '0.5rem' }}>
          {!showAll ? (
            <button className={`${style.editButton} ${style.buttonToggle}`} onClick={() => setShowAll(true)}>
              Ver todas las tareas
            </button>
          ) : (
            <button className={`${style.editButton} ${style.buttonToggle}`} onClick={() => setShowAll(false)}>
              Volver a tareas por fecha
            </button>
          )}
        </div>
      </div>

      <div className={style.tasksList}>
        {showAll ? renderAllTasksTable() : renderContent()}
      </div>
    </div>
  );
}

export default Tareas;
