import axios from "axios";
import { useState, useEffect } from "react";

// ✅ La corrección está aquí, en las llaves {}
function Tareas({ token, refreshKey }) {

  const [tasks, setTasks]  = useState([]); // Cambié el nombre a 'tasks' para más claridad

  useEffect(() => {
    const fetchTasks = async () => {
      // Esta validación ahora funcionará correctamente
      if (!token) {
        setTasks([]); 
        return;
      }
      try {
        const res = await axios.get("http://localhost:3000/api/task", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTasks(res.data);
      } catch (err) {
        console.error("Error al obtener las tareas:", err);
        setTasks([]);
      }
    };
    fetchTasks();
  }, [token, refreshKey]); // El array de dependencias está perfecto

  // Pequeños ajustes para mostrar las tareas
  return (
    <div style={{ padding: '20px', color: 'white' }}>
      <h1>Mis Tareas</h1>
      <div>
        {tasks.length === 0 ? (
           <div key={task._id} style={{ border: '1px solid #66fcf1', borderRadius: '8px', padding: '15px', marginBottom: '15px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <p style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>{task.meta}</p>
                <p style={{ color: '#aaa' }}>Urgencia: {task.urgencia}</p>
              </div>
              <div style={{ display: 'flex', gap: '20px', color: '#ccc' }}>
                {/* Formateamos la fecha para que sea más legible */}
                <p>Fecha: {new Date(task.fecha).toLocaleDateString()}</p>
                <p>Horario: {task.horario}</p>
              </div>
            </div>

        ) : (
          tasks.map((task) => (
            // Es importante añadir una 'key' única a cada elemento de un map
             <p>
            Aún no tienes tareas. ¡Añade una para comenzar! NOOOOOO
          </p>
          ))
        )}
      </div>
    </div>
  );
}

export default Tareas;
