// SignupPage.js (Conectado al Backend)

import { useState } from "react";
import axios from "axios";
import style from "../style/Login.module.css";

// 1. Ahora recibe onAuthSuccess para notificar al padre del éxito
function SignupPage({ onSwitchToLogin, onAuthSuccess }) {
  // 2. Estados para cada campo del formulario
  const [usuario, setUsuario] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [repeatPassword, setRepeatPassword] = useState("");
  const [error, setError] = useState("");

  // 3. Función que se ejecuta al enviar el formulario
  const handleSubmit = async (e) => {
    e.preventDefault(); // Evita que la página se recargue
    setError(""); // Limpia errores anteriores

    if (password !== repeatPassword) {
      return setError("Las contraseñas no coinciden");
    }

    try {
      const res = await axios.post("http://localhost:3000/api/auth/signup", {
        // Asumo que tu ruta es /api/auth/signup, ajústala si es necesario
        username: usuario, // Asegúrate de que tu backend espera 'username'
        email,
        password,
      });

      // 4. Si el registro es exitoso, llama a la función del padre con el nuevo token
      onAuthSuccess(res.data.token);

    } catch (err) {
      // Si el backend devuelve un error (ej: "Usuario ya existe"), lo mostramos
      setError(err.response?.data?.error || "Ocurrió un error al registrarse");
    }
  };

  return (
    <div className={style.container}>
      <div className={style.back}>
        <p className={style.title}>Registrate para comenzar</p>
        <div className={style.containerForm}>
          {/* 5. Conectamos el formulario al handleSubmit */}
          <form onSubmit={handleSubmit}>
            <div className={style.containerInput}>
              <label htmlFor="name">Usuario:</label>
              {/* 6. Conectamos cada input a su estado */}
              <input type="text" value={usuario} onChange={(e) => setUsuario(e.target.value)} required />
            </div>
            <div className={style.containerInput}>
              <label htmlFor="email">Email:</label>
              <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
            </div>
            <div className={style.containerInput}>
              <label htmlFor="password">Contraseña:</label>
              <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
            </div>
            <div className={style.containerInput}>
              <label htmlFor="password">Repetir Contraseña:</label>
              <input type="password" value={repeatPassword} onChange={(e) => setRepeatPassword(e.target.value)} required />
            </div>
            <div className={style.containerRegister}>
              {/* 7. El botón ahora es de tipo "submit" */}
              <button type="submit" className={style.btn}>Registrate</button>
              {error && <p style={{ color: 'red' }}>{error}</p>} {/* Muestra mensajes de error */}
              <div className={style.containerLogOut} onClick={onSwitchToLogin}>
                <p>Ya tengo una cuenta</p>
                <img className={style.logOutImg} src="./logOut.png" alt="logout" />
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default SignupPage;
