import 'react-datepicker/dist/react-datepicker.css';
import style from '../style/LeftsideNotas.module.css';
import { useState } from 'react';
import axios from 'axios';
import { forwardRef } from 'react';
import DatePicker from 'react-datepicker';



function LeftSideNotas() {

  const [isOpen, setIsOpen] = useState(true);
  const toggleContainer = () => setIsOpen(!isOpen);

  // 1. Estado para manejar todos los datos del formulario
  const [formData, setFormData] = useState({
    meta: '',
    fecha: new Date().toISOString().slice(0, 10),
    horario: '12:00',
    urgencia: 'importante',
    color: 'color1',
    esRecurrente: false,
  });

  // Estados para mostrar mensajes al usuario
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  // 2. Función para actualizar el estado cuando un input cambia
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value,
    }));
  };

  // 3. Función que se ejecuta al enviar el formulario
  const handleSubmit = async (e) => {
    e.preventDefault(); // Evita que la página se recargue
    setError('');
    setSuccess('');
    setLoading(true);

    if (!formData.meta) {
      setError('Por favor, escribe el nombre de la meta.');
      setLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No estás autenticado. Por favor, inicia sesión.');
        setLoading(false);
        return;
      }

      // Hacemos la petición POST a la ruta del backend '/api/task'
      await axios.post('http://localhost:3000/api/task', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setSuccess('¡Hábito/Tarea añadido con éxito!');
      // Limpiamos el formulario
      setFormData({
        meta: '',
        fecha: new Date().toISOString().slice(0, 10),
        horario: '12:00',
        urgencia: 'importante',
      });

    } catch (err) {
      setError(err.response?.data?.message || 'Hubo un error al guardar.');
    } finally {
      setLoading(false);
    }
  };


  // Creamos nuestro componente de input personalizado
  const CustomDateInput = forwardRef(({ value, onClick }, ref) => (
    <button className={style.customInputButton} onClick={onClick} ref={ref}>
      {value}
      <span className={style.customInputIcon}>📅</span>
    </button>
  ));
  // Le damos un nombre para debugging
  CustomDateInput.displayName = 'CustomDateInput';


  // 2. Nueva función para manejar la selección de color
  const handleColorSelect = (colorName) => {
    setFormData(prevData => ({
      ...prevData,
      color: colorName,
    }));
  };



  return (
    <div className={`${style.container} ${!isOpen ? style.closed : ''}`}>
      <div className={style.containerOpenClose}>
        <img
          className={style.close}
          src={isOpen ? "./close.png" : "./open.png"}
          alt={isOpen ? "close-tab" : "open-tab"}
          onClick={toggleContainer}
        />
      </div>

      {isOpen && (
        <>
          <div className={style.containerInfo}>
            <p className={style.titleKeep}>Crea un habito</p>
          </div>
          <div className={style.containerForm}>
            {/* 4. Conectamos el <form> con nuestra función handleSubmit */}
            <form onSubmit={handleSubmit}>
              <input
                name="meta"
                type="text"
                placeholder='Escriba su meta'
                value={formData.meta}
                onChange={handleChange}
              />


              <DatePicker
                name="fecha"
                type="date"
                value={formData.fecha}
                onChange={handleChange}
                className={style.datePicker}
              />

              <input
                name="horario"
                type="time"
                value={formData.horario}
                onChange={handleChange}
                className={style.datePicker}
              />
              <select
                name="urgencia"
                value={formData.urgencia}
                onChange={handleChange}
                className={style.select}
              >
                <option value="importante">Importante</option>
                <option value="urgente">Urgente</option>
                <option value="no importante">No Importante</option>
                <option value="obligaciones">Obligaciones</option>
              </select>

              <div>
                <p className={style.subtitle}>Seleccione un color para su tarea</p>
                <div className={style.containerColors}>

                  {/* 3. Hacemos los círculos interactivos */}
                  <div
                    className={`${style.circleOne} ${formData.color === 'color1' ? style.selected : ''}`}
                    onClick={() => handleColorSelect('color1')}
                  ></div>
                  <div
                    className={`${style.circleTwo} ${formData.color === 'color2' ? style.selected : ''}`}
                    onClick={() => handleColorSelect('color2')}
                  ></div>
                  <div
                    className={`${style.circleThree} ${formData.color === 'color3' ? style.selected : ''}`}
                    onClick={() => handleColorSelect('color3')}
                  ></div>
                  <div
                    className={`${style.circleFour} ${formData.color === 'color4' ? style.selected : ''}`}
                    onClick={() => handleColorSelect('color4')}
                  ></div>

                </div>
              </div>
              <div>
                <div className={style.containerKeep}>
                  <p className={style.keep}>Repetir diariamente</p>
                  <input type='checkbox' />
                </div>
              </div>
              <div>
                {/* 5. El botón es de tipo "submit" y se deshabilita mientras carga */}
                <button className={style.btn} type="submit" disabled={loading}>
                  <p>{loading ? 'Guardando...' : 'Añade un habito'}</p>
                </button>
              </div>
            </form>

            {/* Mostramos los mensajes de error o éxito al usuario */}
            {error && <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>}
            {success && <p style={{ color: 'green', marginTop: '10px' }}>{success}</p>}
          </div>
        </>
      )}
    </div>
  );
}

export default LeftSideNotas;
