import { useState, useEffect } from "react";
import axios from "axios";
// 1. Asegúrate de importar 'Outlet' para las rutas anidadas
import { Routes, Route, Navigate, Outlet } from "react-router-dom";

// Componentes de la aplicación
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
import Notas from "./Notas"; // <-- Importa tu nuevo componente de Notas

// Estilos
import style from "../style/App.module.css";

// ====================================================================
// COMPONENTES AUXILIARES PARA LA ESTRUCTURA DE RUTAS
// ====================================================================

/**
 * 2. Componente Dashboard: Agrupa la vista principal que ya tenías.
 * Esto hace que el código de rutas sea más limpio.
 */
function Dashboard({ token, onAuthSuccess, onLoginClick, onCloseModal, activeView, onMovementAdded, movimientos, refreshKey }) {
  return (
    <>
      <LeftsSite movimientos={movimientos} />
      <div className={style.contentSide}>
        <Results
          token={token}
          onAuthSuccess={onAuthSuccess}
          onLoginClick={onLoginClick}
          onCloseModal={onCloseModal}
          activeView={activeView}
          movimientos={movimientos}
          refreshKey={refreshKey}
        />
        <Add onMovementAdded={onMovementAdded} />
      </div>
    </>
  );
}

/**
 * 3. Layout Protegido: Este es el "caparazón" de tu aplicación.
 * Muestra el Nav siempre y un <Outlet /> que renderizará el
 * componente hijo (Dashboard, Notas, etc.) según la URL.
 */
function PrivateLayout({ token, onLogout }) {
  // Si no hay token, no se muestra este layout y se redirige al login
  if (!token) {
    return <Navigate to="/login" />;
  }

  return (
    <div className={style.container}>
      <Nav token={token} onLogout={onLogout} />
      <main className={style.content}>
        {/* Aquí se renderizarán las rutas hijas (Dashboard o Notas) */}
        <Outlet />
      </main>
    </div>
  );
}

/**
 * 4. Componente de Login: Una página simple para cuando el usuario no está logueado.
 * Aquí puedes poner tu formulario de login o un mensaje de bienvenida.
 */
function LoginPage({ onLoginClick }) {
    // Podrías reutilizar el modal de Results aquí o tener una página dedicada.
    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh' }}>
            <h1>Bienvenido a Growth</h1>
            <p>Por favor, inicia sesión para continuar.</p>
            <button onClick={onLoginClick} style={{ padding: '10px 20px', fontSize: '16px' }}>
                Iniciar Sesión
            </button>
        </div>
    );
}


// ====================================================================
// COMPONENTE PRINCIPAL APP
// ====================================================================

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [activeView, setActiveView] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);
  const [movimientos, setMovimientos] = useState([]);

  useEffect(() => {
    const fetchMovimientos = async () => {
      if (!token) {
        setMovimientos([]);
        return;
      }
      try {
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        setMovimientos([]);
      }
    };
    fetchMovimientos();
  }, [token, refreshKey]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
  };

  const handleMovementAdded = () => setRefreshKey(prevKey => prevKey + 1);

  const handleAuthSuccess = (newToken) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
    setActiveView(null);
    // Navegación se maneja automáticamente por el cambio de estado del token
  };

  const handleLoginClick = () => setActiveView('login');
  const handleCloseModal = () => setActiveView(null);

  // 5. La estructura de <Routes> ahora es más clara
  return (
    <Routes>
      {/* RUTAS PÚBLICAS */}
      <Route
        path="/login"
        element={
            // Si ya hay token, no mostramos el login, redirigimos al inicio
            token ? <Navigate to="/" /> : <LoginPage onLoginClick={handleLoginClick} />
        }
      />

      {/* RUTA PADRE PROTEGIDA */}
      <Route element={<PrivateLayout token={token} onLogout={handleLogout} />}>
        {/* Rutas Hijas que se renderizarán dentro de PrivateLayout */}
        <Route
          path="/"
          element={
            <Dashboard
              token={token}
              onAuthSuccess={handleAuthSuccess}
              onLoginClick={handleLoginClick}
              onCloseModal={handleCloseModal}
              activeView={activeView}
              onMovementAdded={handleMovementAdded}
              movimientos={movimientos}
              refreshKey={refreshKey}
            />
          }
        />
        <Route path="/notas" element={<Notas />} />
        {/* Podrías agregar más rutas protegidas aquí, como /tareas, /ajustes, etc. */}
      </Route>

      {/* Redirección por defecto para cualquier otra ruta */}
      <Route path="*" element={<Navigate to={token ? "/" : "/login"} />} />
    </Routes>
  );
}

export default App;
