// App.js (Versión Final sin Dashboard.jsx)

import { useState, useEffect } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import axios from "axios";

// Importamos los componentes necesarios
import ProtectedLayout from "./ProtectedLayout";
import Notas from "./Notas";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
import style from "../style/App.module.css";

// NO importamos Dashboard, ya que no lo usaremos

function App() {
  // --- Toda la lógica de estado y efectos se mantiene igual ---
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [activeView, setActiveView] = useState('signup');
  const [movimientos, setMovimientos] = useState([]);
  const [keyToRefresh, setKeyToRefresh] = useState(0);

  useEffect(() => {
    if (!token) {
      setMovimientos([]);
      return;
    }
    const fetchMovimientos = async () => {
      try {
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        if (err.response && (err.response.status === 401 || err.response.status === 403)) {
          handleLogout();
        }
      }
    };
    fetchMovimientos();
  }, [token, keyToRefresh]);

  const handleAuthSuccess = (newToken) => {
    if (typeof newToken === 'string' && newToken.length > 0) {
      localStorage.setItem("token", newToken);
      setToken(newToken);
    } else {
      console.error("Error: Se intentó guardar un token inválido:", newToken);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
  };

  const handleLoginClick = () => setActiveView('login');
  const handleCloseModal = () => setActiveView('signup');
  const refreshMovimientos = () => setKeyToRefresh(prevKey => prevKey + 1);

  return (
    <Routes>
      {/* --- RUTA PÚBLICA (LOGIN/REGISTRO) --- */}
      <Route
        path="/auth"
        element={
          !token ? (
            <Results
              token={token}
              onAuthSuccess={handleAuthSuccess}
              onLoginClick={handleLoginClick}
              onCloseModal={handleCloseModal}
              activeView={activeView}
            />
          ) : (
            <Navigate to="/" />
          )
        }
      />

      {/* --- RUTAS PROTEGIDAS (DENTRO DEL LOGIN) --- */}
      <Route
        path="/"
        element={
          token ? (
            <ProtectedLayout token={token} onLogout={handleLogout} />
          ) : (
            <Navigate to="/auth" />
          )
        }
      >
        {/* 👇 AQUÍ ESTÁ EL CAMBIO PRINCIPAL 👇 */}
        {/* En lugar de <Dashboard />, ponemos su contenido directamente */}
        <Route
          index
          element={
            <>
              <LeftsSite movimientos={movimientos} />
              <div className={style.contentSide}>
                <Results token={token} movimientos={movimientos} />
                <Add token={token} onMovementAdded={refreshMovimientos} />
              </div>
            </>
          }
        />

        {/* La ruta de Notas sigue funcionando igual */}
        <Route path="notas" element={<Notas />} />

      </Route>

      <Route path="*" element={<Navigate to={token ? "/" : "/auth"} />} />
    </Routes>
  );
}

export default App;
