// src/App.js (CORREGIDO Y COMPLETO)

import { useState, useEffect } from "react";
import axios from "axios"; // Asegúrate de tener el BrowserRouter si no está en index.js
import AppRoutes from "./AppRoutes";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [activeView, setActiveView] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);
  const [movimientos, setMovimientos] = useState([]);
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [movementToEdit, setMovementToEdit] = useState(null);

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

  useEffect(() => {
    const fetchMovimientos = async () => {
      if (!token) {
        setMovimientos([]);
        return;
      }
      try {
        const res = await axios.get(`${API_URL}/api/add`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        setMovimientos([]);
      }
    };
    fetchMovimientos();
  }, [token, refreshKey, API_URL]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
    // Opcional: redirigir al home
  };

  const handleDataRefresh = () => {
    setMovementToEdit(null);
    setRefreshKey((prevKey) => prevKey + 1);
  };

  const handleTaskUpdate = () => {
    setTaskToEdit(null);
    handleDataRefresh();
  };

  // --- FUNCIÓN CORREGIDA ---
  const handleAuthSuccess = (newToken) => {
    localStorage.setItem("token", newToken); // <-- 1. GUARDA el token para que sea persistente
    setToken(newToken);                     // <-- 2. ACTUALIZA el estado para el re-renderizado
    setActiveView(null);                    // <-- 3. Cierra el modal/vista de login
  };

  const handleLoginClick = () => setActiveView("login");
  const handleCloseModal = () => setActiveView(null);

  return (

      <AppRoutes
        token={token}
        onAuthSuccess={handleAuthSuccess}
        onLoginClick={handleLoginClick}
        onCloseModal={handleCloseModal}
        activeView={activeView}
        onMovementAdded={handleDataRefresh}
        onLogout={handleLogout}
        movimientos={movimientos}
        refreshKey={refreshKey}
        taskToEdit={taskToEdit}
        setTaskToEdit={setTaskToEdit}
        onTaskUpdate={handleTaskUpdate}
        movementToEdit={movementToEdit}
        setMovementToEdit={setMovementToEdit}
      />

  );
}

export default App;
