// App.js (CORREGIDO Y COMPLETO)

import { useState, useEffect } from "react"; // <-- ASEGÚRATE DE IMPORTAR useEffect
import axios from "axios"; // <-- ASEGÚRATE DE IMPORTAR axios
import { Routes, Route, Navigate } from "react-router-dom";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite"; // <-- Nota: El componente se llama LeftSite, no LeftsSite
import Add from "./Add";
import Results from "./Results";
import style from "../style/App.module.css";


function PrivateRoute({ children }) { return children; }


function PrivateLayout({ token, onAuthSuccess, onLoginClick, onCloseModal, activeView, onMovementAdded, onLogout, movimientos, refreshKey }) {
  return (
    <div className={style.container}>
      <Nav token={token} onLogout={onLogout} />
      <div className={style.content}>
        {/* Le pasamos la lista de movimientos a LeftSite */}
        <LeftsSite movimientos={movimientos} />
        <div className={style.contentSide}>
          <Results
            token={token}
            onAuthSuccess={onAuthSuccess}
            onLoginClick={onLoginClick}
            onCloseModal={onCloseModal}
            activeView={activeView}
            movimientos={movimientos} // <-- Le pasamos los movimientos a Results
            refreshKey={refreshKey}
          />
          <Add onMovementAdded={onMovementAdded} />
        </div>
      </div>
    </div>
  );
}

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [activeView, setActiveView] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  // 1. EL ESTADO DE 'movimientos' AHORA VIVE AQUÍ, EN EL PADRE
  const [movimientos, setMovimientos] = useState([]);

  // 2. EL useEffect QUE BUSCA LOS DATOS AHORA VIVE AQUÍ
  useEffect(() => {
    const fetchMovimientos = async () => {
      if (!token) {
        setMovimientos([]); // Si no hay token, la lista se vacía
        return;
      }
      try {
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMovimientos(res.data); // Guardamos los datos en el estado del padre
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        setMovimientos([]);
      }
    };
    fetchMovimientos();
  }, [token, refreshKey]); // Se ejecuta si el token o la refreshKey cambian

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
  };
  const handleMovementAdded = () => setRefreshKey(prevKey => prevKey + 1);
  const handleAuthSuccess = (newToken) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
    setActiveView(null);
  };
  const handleLoginClick = () => setActiveView('login');
  const handleCloseModal = () => setActiveView(null);

  return (
    <Routes>
      <Route
        path="/"
        element={
          <PrivateRoute>
            <PrivateLayout
              token={token}
              onAuthSuccess={handleAuthSuccess}
              onLoginClick={handleLoginClick}
              onCloseModal={handleCloseModal}
              activeView={activeView}
              onMovementAdded={handleMovementAdded}
              onLogout={handleLogout}
              movimientos={movimientos} 
              refreshKey={refreshKey}
            />
          </PrivateRoute>
        }
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
