// src/AppRoutes.jsx (VERSIÓN FINAL)

import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import MainLayout from './MainLayout';
import Tareas from './Tareas';
import Dashboard from './Dashboard';

function AppRoutes({ token, onAuthSuccess, onLogout, taskToEdit, setTaskToEdit, onTaskUpdate, refreshKey }) {
  return (
    <Routes>
      <Route
        path="/"
        element={
          <MainLayout 
            token={token} 
            onLogout={onLogout} 
            // Pasamos las props de edición al Layout para el sidebar
            taskToEdit={taskToEdit}
            onTaskUpdate={onTaskUpdate}
          />
        }
      >
        <Route 
          index 
          element={<Dashboard token={token} onAuthSuccess={onAuthSuccess} />} 
        />
        <Route
          path="notas"
          element={
            token ? (
              <Tareas 
                token={token} 
                refreshKey={refreshKey} 
                onEditClick={setTaskToEdit} 
              />
            ) : (
              <Navigate to="/" />
            )
          }
        />
      </Route>
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default AppRoutes;
