import { useState, useMemo } from 'react';
import axios from 'axios';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import style from '../style/Results.module.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

function Results({ 
  token, 
  onAuthSuccess, 
  onLoginClick, 
  onCloseModal, 
  activeView, 
  onEditClick, 
  movimientos, 
  onMovementUpdate, 
  onShowAllChange 
}) {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showAll, setShowAll] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // Filtrar movimientos por fecha
  const filteredMovimientos = useMemo(() => {
    if (!Array.isArray(movimientos)) return [];

    if (showAll) return movimientos;

    const localSelectedDate = new Date(selectedDate);
    localSelectedDate.setMinutes(localSelectedDate.getMinutes() - localSelectedDate.getTimezoneOffset());
    const formattedSelectedDate = localSelectedDate.toISOString().slice(0, 10);

    return movimientos.filter(mov => mov.fecha.slice(0, 10) === formattedSelectedDate);
  }, [movimientos, selectedDate, showAll]);

  // Calcular totales
  const totales = useMemo(() => {
    const ingresos = filteredMovimientos
      .filter(mov => mov.tipo === 'ingreso')
      .reduce((sum, mov) => sum + mov.monto, 0);

    const egresos = filteredMovimientos
      .filter(mov => mov.tipo === 'egreso')
      .reduce((sum, mov) => sum + mov.monto, 0);

    const balance = ingresos - egresos;

    return { ingresos, egresos, balance };
  }, [filteredMovimientos]);

  // Manejadores de eventos
  const handleDateChange = (date) => {
    setSelectedDate(date);
    setShowAll(false);
    if (onShowAllChange) onShowAllChange(false);
  };

  const handleAllMovimientos = async () => {
    if (!token) return;

    setIsLoading(true);
    try {
      const res = await axios.get(`${API_URL}/api/add/all`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.data && onMovementUpdate) {
        onMovementUpdate(res.data);
      }
      setShowAll(true);
      if (onShowAllChange) onShowAllChange(true);
    } catch (err) {
      console.error("Error al obtener todos los movimientos:", err);
      alert("No se pudieron obtener los movimientos.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteMovimiento = async (movimientoId) => {
    if (!window.confirm("¿Estás seguro de que quieres eliminar este movimiento?")) return;

    if (!token) return;

    try {
      await axios.delete(`${API_URL}/api/add/${movimientoId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (onMovementUpdate) {
        onMovementUpdate();
      }
    } catch (err) {
      console.error("Error al eliminar el movimiento:", err);
      alert("No se pudo eliminar el movimiento.");
    }
  };

  // Renderizar login/registro si no hay token
  if (!token) {
    return (
      <div className={style.container}>
        {activeView === 'login' ? (
          <LoginPage onClose={onCloseModal} onAuthSuccess={onAuthSuccess} />
        ) : (
          <SignupPage onSwitchToLogin={onLoginClick} onAuthSuccess={onAuthSuccess} />
        )}
      </div>
    );
  }

  return (
    <div className={style.container}>
      {/* Header con controles */}
      <div className={style.header}>
        <h2>{showAll ? 'Todos los Movimientos' : 'Movimientos del Día'}</h2>

        <div className={style.datePickerContainer}>
          {!showAll && (
            <DatePicker
              selected={selectedDate}
              onChange={handleDateChange}
              dateFormat="dd/MM/yyyy"
              className={style.datePicker}
              maxDate={new Date()}
            />
          )}

          <button 
            onClick={() => {
              if (showAll) {
                setShowAll(false);
                if (onShowAllChange) onShowAllChange(false);
              } else {
                handleAllMovimientos();
              }
            }}
            className={style.icon}
            disabled={isLoading}
            aria-label={showAll ? "Ver movimientos del día" : "Ver todos los movimientos"}
          >
            <img 
              src={showAll ? "/cuadrado.png" : "/list.png"} 
              alt={showAll ? "Vista día" : "Vista completa"} 
            />
          </button>
        </div>
      </div>

      {/* Mostrar totales */}
      {!showAll && filteredMovimientos.length > 0 && (
        <div className={style.totalesContainer}>
          <div className={style.totalItem}>
            <span>Ingresos:</span>
            <span className={style.totalIngreso}>+${totales.ingresos.toFixed(2)}</span>
          </div>
          <div className={style.totalItem}>
            <span>Egresos:</span>
            <span className={style.totalEgreso}>-${totales.egresos.toFixed(2)}</span>
          </div>
          <div className={style.totalItem}>
            <span>Balance:</span>
            <span className={totales.balance >= 0 ? style.totalIngreso : style.totalEgreso}>
              ${totales.balance.toFixed(2)}
            </span>
          </div>
        </div>
      )}

      {/* Contenido principal */}
      {showAll ? (
        // Vista de tabla completa
        <div className={style.allMovimientosContainer}>
          {isLoading ? (
            <div className={style.loadingContainer}>
              <p>Cargando movimientos...</p>
            </div>
          ) : movimientos.length === 0 ? (
            <p className={style.noMovimientos}>No tienes movimientos registrados.</p>
          ) : (
            <table className={style.movimientosTable}>
              <thead className={style.titleMovimientos}>
                <tr>
                  <th>Fecha</th>
                  <th>Tipo</th>
                  <th>Categoría</th>
                  <th>Detalle</th>
                  <th>Monto</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {movimientos.map((mov, index) => (
                  <tr 
                    key={mov._id} 
                    className={`${style.rowText} ${style[`color${(index % 4) + 1}`]}`}
                  >
                    <td>{new Date(mov.fecha).toLocaleDateString('es-ES')}</td>
                    <td>{mov.tipo === 'ingreso' ? '➕' : '➖'}</td>
                    <td className={style.category}>{mov.categoria}</td>
                    <td>{mov.detalle}</td>
                    <td className={mov.tipo === 'ingreso' ? style.montoIngreso : style.montoEgreso}>
                      {mov.tipo === 'ingreso' ? '+' : '-'}${mov.monto.toFixed(2)}
                    </td>
                    <td>
                      <button 
                        onClick={() => onEditClick(mov)}
                        aria-label="Editar movimiento"
                      >
                        Editar
                      </button>
                      <button 
                        onClick={() => handleDeleteMovimiento(mov._id)}
                        aria-label="Eliminar movimiento"
                      >
                        Borrar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      ) : (
        // Vista de tarjetas por día
        <div className={style.containerInfoAll}>
          {filteredMovimientos.length === 0 ? (
            <p className={style.noMovimientos}>
              No tienes movimientos para esta fecha.
            </p>
          ) : (
            filteredMovimientos.map((mov) => (
              <div
                className={`${style.containerInfo} ${
                  mov.tipo === 'ingreso' ? style.bordeIngreso : style.bordeEgreso
                }`}
                key={mov._id}
              >
                <div className={style.cardInner}>
                  {/* Cara frontal */}
                  <div className={style.cardFront}>
                    <div className={style.info}>
                      <p className={style.category}>{mov.categoria}</p>
                      <p className={style.detalle}>{mov.detalle}</p>
                      <p className={style.fecha}>
                        {new Date(mov.fecha).toLocaleTimeString([], { 
                          hour: '2-digit', 
                          minute: '2-digit' 
                        })}
                      </p>
                    </div>
                    <div className={style.montoContainer}>
                      <p className={`${style.monto} ${
                        mov.tipo === 'ingreso' ? style.montoIngreso : style.montoEgreso
                      }`}>
                        {mov.tipo === 'ingreso' ? '+' : '-'}${mov.monto.toFixed(2)}
                      </p>
                      <img
                        className={style.arrowResult}
                        src={mov.tipo === 'ingreso' ? '/arrowGreen.png' : '/arrowRed.png'}
                        alt={mov.tipo === 'ingreso' ? 'Ingreso' : 'Egreso'}
                        aria-hidden="true"
                      />
                    </div>
                  </div>

                  {/* Cara trasera */}
                  <div className={style.cardBack}>
                    <p className={style.deletePromptText}>
                      ¿Qué deseas hacer con este movimiento?
                    </p>
                    <div className={style.containerButton}>
                      <button 
                        onClick={() => onEditClick(mov)} 
                        className={style.deleteButton}
                        aria-label="Editar movimiento"
                      >
                        <img className={style.ButtonImg} src="/edit.png" alt="Editar" />
                      </button>
                      <button
                        onClick={() => handleDeleteMovimiento(mov._id)}
                        className={style.deleteButton}
                        aria-label="Eliminar movimiento"
                      >
                        <img className={style.ButtonImg} src="/trush.png" alt="Eliminar" />
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default Results;
