import { useState } from 'react';
import { BrowserRouter } from 'react-router-dom';import AppRoutes from './AppRoutes';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  // La única función que maneja la actualización de tareas
  const handleTaskUpdate = () => {
    setTaskToEdit(null);
    setRefreshKey(prevKey => prevKey + 1);
  };

  // El resto de tus estados y funciones de App...
  const [activeView, setActiveView] = useState(null);
  const handleAuthSuccess = (newToken) => { setToken(newToken); setActiveView(null); };
  const handleLogout = () => { localStorage.removeItem('token'); setToken(null); };
  const handleLoginClick = () => setActiveView("login");
  const handleCloseModal = () => setActiveView(null);

  return (

      <AppRoutes
        token={token}
        onAuthSuccess={handleAuthSuccess}
        onLogout={handleLogout}
        onLoginClick={handleLoginClick}
        onCloseModal={handleCloseModal}
        activeView={activeView}

        // --- Pasamos las props de tareas con nombres consistentes ---
        taskToEdit={taskToEdit}
        setTaskToEdit={setTaskToEdit}
        onTaskUpdate={handleTaskUpdate}
        refreshKey={refreshKey}
      />

  );
}

export default App;
