import axios from "axios";
import { useState, useEffect } from "react";

function Tareas(token, refreshKey) {

const [task, setTask]  = useState([])

  useEffect(() => {
    const fetchTask = async () => {
      if (!token) {
        setTask([]); 
        return;
      }
      try {
        const res = await axios.get("http://localhost:3000/api/task", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTask(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        setTask([]); // Si el token es malo, vaciamos la lista
      }
    };
    fetchTask();
  }, [token, refreshKey]);
  return (
    <div>
          <div>
          {task.length === 0 ? (
            <p >
              Aún no tienes movimientos. ¡Añade uno para comenzar!
            </p>
          ) : (
            task.map((mov) => (
              <div >
                <div >
                  <p >{mov.categoria}</p>
                  <p >{mov.detalle}</p>
                </div>
                <p >{mov.monto}</p>
              </div>
            ))
          )}
        </div>
    </div>
  );
}

export default Tareas;
