// App.js (Versión final para autenticación)

import { useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
import style from "../style/App.module.css";

function PrivateRoute({ children }) { return children; }

function PrivateLayout({ onLoginClick, onCloseModal, activeView, token, onAuthSuccess }) { // <-- Recibe token y onAuthSuccess
  return (
    <div className={style.container}>
      <Nav />
      <div className={style.content}>
        <LeftsSite />
        <div className={style.contentSide}>
          <Results
            onLoginClick={onLoginClick}
            onCloseModal={onCloseModal}
            activeView={activeView}
            token={token} // <-- Pasa el token a Results
            onAuthSuccess={onAuthSuccess} // <-- Pasa la función de éxito a Results
          />
          <Add />
        </div>
      </div>
    </div>
  );
}

function App() {
  // 1. ESTADO DE AUTENTICACIÓN: Revisa localStorage para ver si ya existe un token.
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [activeView, setActiveView] = useState(null);

  // 2. FUNCIÓN DE ÉXITO: Se llamará desde Login/Signup cuando el backend devuelva un token.
  const handleAuthSuccess = (newToken) => {
    localStorage.setItem("token", newToken); // Guarda el token en el navegador
    setToken(newToken); // Actualiza el estado de la aplicación
  };

  const handleLoginClick = () => { setActiveView('login'); };
  const handleCloseModal = () => { setActiveView(null); };

  return (
    <Routes>
      <Route
        path="/"
        element={
          <PrivateRoute>
            <PrivateLayout
              onLoginClick={handleLoginClick}
              onCloseModal={handleCloseModal}
              activeView={activeView}
              token={token} // <-- Pasa el token actual
              onAuthSuccess={handleAuthSuccess} // <-- Pasa la función de éxito
            />
          </PrivateRoute>
        }
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
