import axios from "axios";
import { useState, useEffect } from "react";

function Tareas({token, refreshKey}) {

const [task, setTask]  = useState([])

  useEffect(() => {
    const fetchTask = async () => {
      if (!token) {
        setTask([]); 
        return;
      }
      try {
        const res = await axios.get("http://localhost:3000/api/task", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTask(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        setTask([]); // Si el token es malo, vaciamos la lista
      }
    };
    fetchTask();
  }, [token, refreshKey]);
  return (
    <div style={{ padding: '20px', color: 'white' }}>
      <h1>Mis Tareas</h1>
      <div>
        {tasks.length === 0 ? (
          <p>
            Aún no tienes tareas. ¡Añade una para comenzar!
          </p>
        ) : (
          tasks.map((task) => (
            // Es importante añadir una 'key' única a cada elemento de un map
            <div key={task._id} style={{ border: '1px solid #66fcf1', borderRadius: '8px', padding: '15px', marginBottom: '15px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <p style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>{task.meta}</p>
                <p style={{ color: '#aaa' }}>Urgencia: {task.urgencia}</p>
              </div>
              <div style={{ display: 'flex', gap: '20px', color: '#ccc' }}>
                {/* Formateamos la fecha para que sea más legible */}
                <p>Fecha: {new Date(task.fecha).toLocaleDateString()}</p>
                <p>Horario: {task.horario}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Tareas;
