// App.js (Modificado)

import { useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
//import LoginPage from "./LoginPage";
//import SignupPage from "./SignupPage";
import style from "../style/App.module.css";

function PrivateRoute({ children }) { return children; }

// Layout anidado
function PrivateLayout({ onRegisterClick, onCloseModal, activeView }) {
  return (
    <div className={style.container}>
      <Nav />
      <div className={style.content}>
        <LeftsSite />
        <div className={style.contentSide}>
          <Results
            onRegisterClick={onRegisterClick}
            onCloseModal={onCloseModal} // <-- Pasamos la función de cerrar
            activeView={activeView}
          />
          <Add />
        </div>
      </div>
    </div>
  );
}

function App() {
  // El estado ahora solo se preocupa del pop-up de registro
  const [activeView, setActiveView] = useState(null);

  // Solo necesitamos la función para ABRIR el pop-up de registro
  const handleRegisterClick = () => {
    setActiveView('signup');
  };

  // Y una función para CERRAR cualquier modal que esté abierto
  const handleCloseModal = () => {
    setActiveView(null);
  };

  return (
    <Routes>
      {/* ... tus otras rutas ... */}
      <Route
        path="/"
        element={
          <PrivateRoute>
            <PrivateLayout
              onRegisterClick={handleRegisterClick}
              onCloseModal={handleCloseModal} // <-- Pasamos la nueva función
              activeView={activeView}
            />
          </PrivateRoute>
        }
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
