import style from '../style/Results.module.css'
import { useEffect, useState } from 'react';
import axios from 'axios';

function Results() {
  const [movimientos, setMovimientos] = useState([]);

  useEffect(() => {
    const fetchMovimientos = async () => {
      try {
        const res = await axios.get("http://localhost:3000/api/add");
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
      }
    };

    fetchMovimientos();
  }, []);
  return (
    <div className={style.container}>
           <div>
      <h2>Movimientos</h2>
      {movimientos.length === 0 && <p>No hay movimientos</p>}
      <div className={style.containerInfoAll}>
        {movimientos.map((mov) => (
       <div   className={`${style.containerInfo} ${
        mov.tipo === 'ingreso' ? style.bordeIngreso : style.bordeEgreso
      }`}  key={mov._id}>
            <p> {mov.categoria.toUpperCase()}</p>
            <p> {mov.detalle} </p>
            <p> ${mov.monto} </p>
        </div>
        ))}
      </div>
    </div>
    </div>
  )
}

export default Results
