import 'react-datepicker/dist/react-datepicker.css';
import style from '../style/LeftsideNotas.module.css';
import { useState } from 'react';
import axios from 'axios';
import { forwardRef } from 'react';
import DatePicker from 'react-datepicker';

function LeftSideNotas({ onTaskAdded }) { // Renombramos la prop para más claridad

  const [isOpen, setIsOpen] = useState(true);
  const toggleContainer = () => setIsOpen(!isOpen);

  // --- CAMBIO 1: Añadimos 'esRecurrente' al estado inicial ---
  const initialFormData = {
    meta: '',
    fecha: new Date(), // Usamos objeto Date para el DatePicker
    horario: '12:00',
    urgencia: 'importante',
    color: 'color1',
    esRecurrente: false, // El nuevo campo
  };

  const [formData, setFormData] = useState(initialFormData);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  // --- CAMBIO 2: Mejoramos handleChange para que funcione con checkboxes ---
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  // Función específica para el DatePicker
  const handleDateChange = (date) => {
    setFormData(prevData => ({
      ...prevData,
      fecha: date,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    if (!formData.meta) {
      setError('Por favor, escribe el nombre de la meta.');
      setLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No estás autenticado. Por favor, inicia sesión.');
        setLoading(false);
        return;
      }

      await axios.post('http://localhost:3000/api/task', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setSuccess('¡Hábito/Tarea añadido con éxito!');
      setFormData(initialFormData); // Reseteamos al estado inicial

      if (onTaskAdded) {
        onTaskAdded(); // Notificamos al componente padre que se añadió una tarea
      }

    } catch (err) {
      setError(err.response?.data?.message || 'Hubo un error al guardar.');
    } finally {
      setLoading(false);
    }
  };

  const handleColorSelect = (colorName) => {
    setFormData(prevData => ({
      ...prevData,
      color: colorName,
    }));
  };

  return (
    <div className={`${style.container} ${!isOpen ? style.closed : ''}`}>
      <div className={style.containerOpenClose}>
        <img
          className={style.close}
          src={isOpen ? "/close.png" : "/open.png"} // Usamos rutas desde public
          alt={isOpen ? "close-tab" : "open-tab"}
          onClick={toggleContainer}
        />
      </div>

      {isOpen && (
        <>
          <div className={style.containerInfo}>
            <p className={style.titleKeep}>Crea un habito</p>
          </div>
          <div className={style.containerForm}>
            <form onSubmit={handleSubmit}>
              <input
                name="meta"
                type="text"
                placeholder='Escriba su meta'
                value={formData.meta}
                onChange={handleChange}
              />

              {/* Ajustamos el DatePicker */}
              <DatePicker
                selected={formData.fecha}
                onChange={handleDateChange} // Usa su propio handler
                dateFormat="dd-MM-yyyy"
                className={style.datePicker}
              />

              <input
                name="horario"
                type="time"
                value={formData.horario}
                onChange={handleChange}
                className={style.datePicker}
              />
              <select
                name="urgencia"
                value={formData.urgencia}
                onChange={handleChange}
                className={style.select}
              >
                <option value="importante">Importante</option>
                <option value="urgente">Urgente</option>
                <option value="no importante">No Importante</option>
                <option value="obligaciones">Obligaciones</option>
              </select>

              <div>
                <p className={style.subtitle}>Seleccione un color para su tarea</p>
                <div className={style.containerColors}>
                   {/* ... tus círculos de colores se mantienen igual ... */}
                </div>
              </div>

              {/* --- CAMBIO 3: Conectamos el checkbox al estado --- */}
              <div className={style.containerKeep}>
                <p className={style.keep}>Repetir diariamente</p>
                <input
                  name="esRecurrente"
                  type='checkbox'
                  checked={formData.esRecurrente}
                  onChange={handleChange}
                />
              </div>

              <div>
                <button className={style.btn} type="submit" disabled={loading}>
                  <p>{loading ? 'Guardando...' : 'Añade un habito'}</p>
                </button>
              </div>
            </form>

            {error && <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>}
            {success && <p style={{ color: 'green', marginTop: '10px' }}>{success}</p>}
          </div>
        </>
      )}
    </div>
  );
}

export default LeftSideNotas;
