import style from '../style/LeftsideNotas.module.css';
import { useState } from 'react';
import axios from 'axios'; // Para hacer la petición al backend

function LeftSideNotas() {
  const [isOpen, setIsOpen] = useState(true);

  // 1. Estado para manejar todos los datos del formulario en un solo objeto
  const [formData, setFormData] = useState({
    meta: '',
    fecha: new Date().toISOString().slice(0, 10), // Fecha de hoy por defecto
    horario: '12:00', // Horario por defecto
    urgencia: 'importante', // Opción por defecto
    color: '', // Para el color seleccionado
    predeterminado: false,
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const toggleContainer = () => setIsOpen(!isOpen);

  // 2. Función que actualiza el estado cuando un input cambia
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  // 3. Función que se ejecuta al enviar el formulario
  const handleSubmit = async (e) => {
    e.preventDefault(); // Evita que la página se recargue
    setError('');
    setSuccess('');

    // Validación simple
    if (!formData.meta) {
      return setError('Por favor, escribe tu meta.');
    }

    try {
      const token = localStorage.getItem('token');
      // Hacemos la petición POST al backend
      const response = await axios.post('http://localhost:3000/api/habitos', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setSuccess('¡Hábito añadido con éxito!');
      // Opcional: Limpiar el formulario después de enviar
      setFormData({
        meta: '',
        fecha: new Date().toISOString().slice(0, 10),
        horario: '12:00',
        urgencia: 'importante',
        color: '',
        predeterminado: false,
      });

    } catch (err) {
      setError(err.response?.data?.message || 'Error al guardar el hábito.');
    }
  };

  return (
    <div className={`${style.container} ${!isOpen ? style.closed : ''}`}>
      <div className={style.containerOpenClose}>
        <img 
          className={style.close} 
          src={isOpen ? "./close.png" : "./open.png"} 
          alt={isOpen ? "close-tab" : "open-tab"} 
          onClick={toggleContainer}
        />
      </div>

      {isOpen && (
        <>  
          <div className={style.containerInfo}>
            <p className={style.titleKeep}>Crea un habito</p>
          </div>
          <div className={style.containerForm}>
            {/* 4. Conectamos el formulario con la función handleSubmit */}
            <form onSubmit={handleSubmit}>
              <input name="meta" type="text" placeholder='Escriba su meta' value={formData.meta} onChange={handleChange} />

              {/* INPUTS CORREGIDOS */}
              <input name="fecha" type="date" placeholder='Fecha' value={formData.fecha} onChange={handleChange} />
              <input name="horario" type="time" placeholder='Horario' value={formData.horario} onChange={handleChange} />

              <select name="urgencia" value={formData.urgencia} onChange={handleChange}>
                <option value="importante">Importante</option>
                <option value="urgente">Urgente</option>
                <option value="no importante">No Importante</option>
                <option value="obligaciones">Obligaciones</option>
              </select>

              <div>
                <p className={style.subtitle}>Seleccione un color para su tarea</p>
                {/* Aquí podrías añadir lógica para seleccionar un color y guardarlo en el estado */}
                <div className={style.containerColors}>
                  <div className={style.circleOne}></div>
                  <div className={style.circleTwo}></div>
                  <div className={style.circleThree}></div>
                  <div className={style.circleFour}></div>
                </div>
              </div>
              <div>
                <div className={style.containerKeep}>
                  <p className={style.keep}>Guardar como predeterminado</p>
                  <input name="predeterminado" type='checkbox' checked={formData.predeterminado} onChange={handleChange} />
                </div>
              </div>
              <div>
                {/* 5. El botón ahora es de tipo "submit" */}
                <button type="submit">
                  <p>Añade un habito</p>
                </button>
              </div>
            </form>
            {error && <p style={{color: 'red', marginTop: '10px'}}>{error}</p>}
            {success && <p style={{color: 'green', marginTop: '10px'}}>{success}</p>}
          </div>
        </>
      )}
    </div>
  );
}

export default LeftSideNotas;
