// App.js

import { useState,useEffect } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Nav from "./Nav";
import LeftsSite from "./LeftsSite";
import Add from "./Add";
import Results from "./Results";
import style from "../style/App.module.css";
import axios from "axios";

function PrivateRoute({ children }) { return children; }

// =======================================================
// LA CORRECCIÓN ESTÁ AQUÍ, EN LA LISTA DE PROPS
// =======================================================
function PrivateLayout({ token, onAuthSuccess, onLoginClick, onCloseModal, activeView, onMovementAdded, refreshKey, onLogout, movimientos }) {
  return (
    <div className={style.container}>
      {/* Ahora 'onLogout' sí está definido y se puede pasar a Nav */}
      <Nav token={token} onLogout={onLogout} />
      <div className={style.content}>
        <LeftsSite />
        <div className={style.contentSide}>
          <Results
            token={token}
            onAuthSuccess={onAuthSuccess}
            onLoginClick={onLoginClick}
            onCloseModal={onCloseModal}
            activeView={activeView}
            refreshKey={refreshKey}
            movimientos={movimientos} 
          />
          <Add onMovementAdded={onMovementAdded} />
        </div>
      </div>
    </div>
  );
}

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [activeView, setActiveView] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);
   const [movimientos, setMovimientos] = useState([]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
  };

  const handleMovementAdded = () => {
    setRefreshKey(prevKey => prevKey + 1);
  };

  const handleAuthSuccess = (newToken) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
    setActiveView(null);
  };

  const handleLoginClick = () => {
    setActiveView('login');
  };

  const handleCloseModal = () => {
    setActiveView(null);
  };

    // 2. EL useEffect QUE BUSCA LOS DATOS TAMBIÉN VIVE AQUÍ
  useEffect(() => {
    const fetchMovimientos = async () => {
      if (!token) {
        setMovimientos([]);
        return;
      }
      try {
        const res = await axios.get("http://localhost:3000/api/add", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMovimientos(res.data);
      } catch (err) {
        console.error("Error al obtener movimientos:", err);
        setMovimientos([]);
      }
    };
    fetchMovimientos();
  }, [token, refreshKey]); // Se actualiza con el token y al añadir un movimiento

  return (
    <Routes>
      <Route
        path="/"
        element={
          <PrivateRoute>
            <PrivateLayout
              token={token}
              onAuthSuccess={handleAuthSuccess}
              onLoginClick={handleLoginClick}
              onCloseModal={handleCloseModal}
              activeView={activeView}
              onMovementAdded={handleMovementAdded}
              refreshKey={refreshKey}
              onLogout={handleLogout}
                 movimientos={movimientos}
            />
          </PrivateRoute>
        }
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default App;
