import { useState } from 'react';
import { register as registerService } from '../api/auth';
import style from '../style/Login.module.css';

const Register = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      setError('Las contraseñas no coinciden');
      return;
    }

    if (formData.password.length < 6) {
      setError('La contraseña debe tener al menos 6 caracteres');
      return;
    }

    setLoading(true);
    setError('');

    try {
      await registerService({
        email: formData.email,
        password: formData.password
      });

      alert('Registro exitoso. Ahora puedes iniciar sesión.');
      window.location.href = '/login';
    } catch (err) {
      setError(err.message || 'Error en el registro');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={style.loginContainer}>
      <div className={style.loginCard}>
        <h2 className={style.title}>Crear Cuenta</h2>

        <form onSubmit={handleSubmit} className={style.loginForm}>
          {error && <div className={style.errorMessage}>{error}</div>}

          <div className={style.formGroup}>
            <label htmlFor="email" className={style.label}>Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              disabled={loading}
              className={style.input}
              placeholder="tu@email.com"
            />
          </div>

          <div className={style.formGroup}>
            <label htmlFor="password" className={style.label}>Contraseña:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              disabled={loading}
              className={style.input}
              placeholder="Mínimo 6 caracteres"
            />
          </div>

          <div className={style.formGroup}>
            <label htmlFor="confirmPassword" className={style.label}>Confirmar Contraseña:</label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
              disabled={loading}
              className={style.input}
              placeholder="Repite tu contraseña"
            />
          </div>

          <button 
            type="submit" 
            disabled={loading}
            className={style.loginButton}
          >
            {loading ? 'Creando cuenta...' : 'Crear Cuenta'}
          </button>
        </form>

        <p className={style.registerLink}>
          ¿Ya tienes cuenta? <a href="/login">Inicia sesión aquí</a>
        </p>
      </div>
    </div>
  );
};

export default Register;
