import axios from "axios";
import { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import style from "../style/Tarea.module.css";
import resultsStyle from "../style/Results.module.css";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:3000";

function Tareas({ token, refreshKey, onEditClick }) {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showList, setShowList] = useState(false);
  const [error, setError] = useState("");
  const [selectedDate, setSelectedDate] = useState(new Date());

  useEffect(() => {
    const fetchTasks = async () => {
      if (!token) return;

      setLoading(true);
      setError("");

      try {
        let url = `${API_URL}/api/task`;

        if (!showList) {
          const d = new Date(selectedDate);
          d.setMinutes(d.getMinutes() - d.getTimezoneOffset());
          url += `?fecha=${d.toISOString().slice(0, 10)}`;
        }

        const res = await axios.get(url, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setTasks(res.data);
      } catch {
        setError("No se pudieron cargar las tareas.");
      } finally {
        setLoading(false);
      }
    };

    fetchTasks();
  }, [token, refreshKey, selectedDate, showList]);

  const handleToggleComplete = async (task) => {
    const res = await axios.put(
      `${API_URL}/api/task/${task._id}/status`,
      { completada: !task.completada },
      { headers: { Authorization: `Bearer ${token}` } }
    );

    setTasks((prev) =>
      prev.map((t) => (t._id === task._id ? res.data : t))
    );
  };

  const handleDeleteTask = async (id) => {
    if (!window.confirm("¿Eliminar tarea?")) return;
    await axios.delete(`${API_URL}/api/task/${id}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks((prev) => prev.filter((t) => t._id !== id));
  };

  /* ------------------ VISTA LISTA ------------------ */
  const renderList = () => {
    if (loading) return <p>Cargando...</p>;
    if (error) return <p className={resultsStyle.error}>{error}</p>;
    if (!tasks.length) return <p>No hay tareas.</p>;

    return (
      <div className={resultsStyle.taskList}>
        {tasks.map((t) => (
          <div
            key={t._id}
            className={`${resultsStyle.taskItem} ${
              resultsStyle[t.color] || ""
            } ${t.completada ? resultsStyle.completed : ""}`}
          >
            <div className={resultsStyle.taskLeft}>
              <p className={resultsStyle.taskTitle}>{t.meta}</p>
              <p className={resultsStyle.taskDate}>
                {t.fecha?.slice(0, 10)} · {t.horario || "--:--"}
              </p>
              <span className={resultsStyle.urgencia}>{t.urgencia}</span>
            </div>

            <div className={resultsStyle.taskRight}>
              <label className={resultsStyle.check}>
                <input
                  type="checkbox"
                  checked={t.completada}
                  onChange={() => handleToggleComplete(t)}
                />
                <span />
              </label>

              <button onClick={() => onEditClick(t)}>Editar</button>
              <button onClick={() => handleDeleteTask(t._id)}>Eliminar</button>
            </div>
          </div>
        ))}
      </div>
    );
  };

  /* ------------------ VISTA POR FECHA ------------------ */
  const renderCards = () => {
    if (loading) return <p>Cargando...</p>;
    if (!tasks.length) return <p>No hay tareas.</p>;

    return tasks.map((task) => (
      <div key={task._id} className={style.taskContainer}>
        {/* tu card actual SIN CAMBIOS */}
      </div>
    ));
  };

  return (
    <div className={style.container}>
      <div className={resultsStyle.header}>
        <h1>{showList ? "Todas las tareas" : "Tareas del día"}</h1>

        {!showList && (
          <DatePicker
            selected={selectedDate}
            onChange={setSelectedDate}
            dateFormat="dd-MM-yyyy"
            className={resultsStyle.datePicker}
          />
        )}

        <button onClick={() => setShowList(!showList)}>
          {showList ? "Ver por fecha" : "Ver en lista"}
        </button>
      </div>

      {showList ? renderList() : renderCards()}
    </div>
  );
}

export default Tareas;
